import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IPageIPolicy } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIPolicy";
import { IPolicy } from "@ORGANIZATION/PROJECT-api/lib/structures/IPolicy";

/**
 * Test retrieving a paginated and filtered list of permission policies.
 *
 * This end-to-end test covers the following:
 * 1. List all permission policies (no filters, default pagination).
 * 2. Search by partial and exact policy name, confirming filter accuracy.
 * 3. Search by conditions, confirming that filters work for JSON/serialized fields.
 * 4. Confirm that an empty result is returned when a query matches no policies.
 * 5. Validate edge-case pagination: first page, last page, and requesting pages beyond the last.
 * 6. Confirm pagination metadata is correct for each case.
 */
export async function test_api_policy_list_with_filter_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. List all permission policies (no filters, page 1, default limit)
  const pageAll: IPageIPolicy = await api.functional.securityCompliance.policy.patch(connection, {
    body: {}
  });
  typia.assert(pageAll);
  TestValidator.equals("pagination first page")(1)(pageAll.pagination.current);
  TestValidator.equals("data is array")(true)(Array.isArray(pageAll.data));

  // Save data for later reference
  const totalPolicies = pageAll.pagination.records;
  const limit = pageAll.pagination.limit;
  const totalPages = pageAll.pagination.pages;
  const hasPolicies = pageAll.data.length > 0;

  // 2. If at least one policy exists, filter by policy name (exact/partial match)
  if (hasPolicies) {
    const policyName = pageAll.data[0].name;
    // Exact match
    const pageByExactName: IPageIPolicy = await api.functional.securityCompliance.policy.patch(connection, {
      body: { name: policyName },
    });
    typia.assert(pageByExactName);
    TestValidator.equals("filter by exact policy name")(true)(
      pageByExactName.data.every((item) => item.name === policyName)
    );
    // Partial/fuzzy match (use substring of name if possible)
    if (policyName.length > 3) {
      const nameSubstring = policyName.slice(0, 3);
      const pageByPartialName: IPageIPolicy = await api.functional.securityCompliance.policy.patch(connection, {
        body: { name: nameSubstring },
      });
      typia.assert(pageByPartialName);
      TestValidator.equals("filter by fuzzy name")(true)(
        pageByPartialName.data.every((item) => item.name.includes(nameSubstring))
      );
    }
    // Filter by condition substring if possible
    const conditionsText = pageAll.data[0].conditions;
    if (conditionsText && conditionsText.length > 3) {
      const condPart = conditionsText.slice(0, 3);
      const pageByCond: IPageIPolicy = await api.functional.securityCompliance.policy.patch(connection, {
        body: { conditions: condPart },
      });
      typia.assert(pageByCond);
      TestValidator.equals("filter by fuzzy conditions")(true)(
        pageByCond.data.every((item) => item.conditions.includes(condPart))
      );
    }
  }

  // 3. Query known-no-match (use improbable string)
  const pageNoMatch: IPageIPolicy = await api.functional.securityCompliance.policy.patch(connection, {
    body: { name: "___never_match_policy_name___" },
  });
  typia.assert(pageNoMatch);
  TestValidator.equals("empty result for impossible filter")(0)(pageNoMatch.data.length);

  // 4. Edge-case pagination: last page number
  if (totalPages > 1) {
    const lastPage: IPageIPolicy = await api.functional.securityCompliance.policy.patch(connection, {
      body: { page: totalPages },
    });
    typia.assert(lastPage);
    TestValidator.equals("last page number")(totalPages)(lastPage.pagination.current);
    TestValidator.equals("last page metadata")(lastPage.pagination.pages)(totalPages);
    // May have < limit items on last page
    TestValidator.equals("last page does not exceed limit")(
      true
    )(lastPage.data.length <= limit);
  }

  // 5. Page number exceeding max
  const excessivePage: IPageIPolicy = await api.functional.securityCompliance.policy.patch(connection, {
    body: { page: totalPages + 1 },
  });
  typia.assert(excessivePage);
  TestValidator.equals("excess page returns empty data")(0)(excessivePage.data.length);
}

