    TestValidator.equals("data not empty")(true)(page1.data.length > 0);
  } else {
    TestValidator.equals("data empty")(0)(page1.data.length);
  }

  // 2. Query with advanced filters: status, report_type, date window (if possible)
  if (page1.data.length > 0) {
    const sample = page1.data[0];
    // Try status and report_type filter with actual data to guarantee hit
    const filtered = await api.functional.securityCompliance.complianceReport.patch(connection, {
      body: {
        status: sample.status,
        report_type: sample.report_type,
        generated_from: sample.generated_at,
        generated_to: sample.generated_at,
      } satisfies IComplianceReport.IRequest,
    });
    typia.assert(filtered);
    // Confirm all data matches filter
    for (const summary of filtered.data) {
      TestValidator.equals("filtered status")(sample.status)(summary.status);
      TestValidator.equals("filtered report_type")(sample.report_type)(summary.report_type);
      TestValidator.equals("in generated range")(
        true,
      )(
        summary.generated_at >= sample.generated_at &&
          summary.generated_at <= sample.generated_at,
      );
    }
  }

  // 3. Query with filter that matches no data (gibberish status)
  const none = await api.functional.securityCompliance.complianceReport.patch(connection, {
    body: { status: "___NON_EXISTENT_STATUS___" },
  });
  typia.assert(none);
  TestValidator.equals("no-results data array")(0)(none.data.length);
  TestValidator.equals("no-results total records")(0)(none.pagination.records);

  // 4. Paginate to next page if more data (boundary test)
  if (page1.data.length > 0 && page1.pagination.records > page1.pagination.limit) {
    const page2 = await api.functional.securityCompliance.complianceReport.patch(connection, {
      body: { },
    });
    typia.assert(page2);
    TestValidator.equals("current page default is 1 again")(1)(page2.pagination.current);
    TestValidator.equals("limit unchanged")(page1.pagination.limit)(page2.pagination.limit);
    // Pagination support would require passing page parameter if available in DTO; skipped if not.
  }
}

