/**
 * Test updating a compliance report for allowed/mutable fields.
 *
 * This test validates successful updates to a compliance report's mutable fields,
 * and verifies error handling when attempting to update immutable fields,
 * sending invalid data, or updating a nonexistent report ID.
 *
 * 1. Create a new compliance report (for update tests)
 * 2. Update the report's mutable fields (status, published_at) and validate changes
 * 3. Attempt to update immutable fields (e.g., id, report_type, generated_at) and confirm failure
 * 4. Attempt update with invalid data (e.g., bad published_at value) and confirm validation error
 * 5. Attempt to update with a nonexistent report ID and expect proper error response
 */
export async function test_api_compliance_report_update_mutable_and_edge_cases(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a new compliance report
  const createBody: IComplianceReport.ICreate = {
    report_type: "GDPR_EXPORT",
    status: "pending",
  };
  const report: IComplianceReport = await api.functional.securityCompliance.complianceReport.post(
    connection,
    { body: createBody }
  );
  typia.assert(report);

  // 2. Update the report's mutable fields (status, published_at)
  const newStatus = "published";
  const newPublishedAt = new Date().toISOString();
  const updateBody: IComplianceReport.IUpdate = {
    status: newStatus,
    published_at: newPublishedAt,
  };
  const updated: IComplianceReport = await api.functional.securityCompliance.complianceReport.putById(
    connection,
    { id: report.id, body: updateBody }
  );
  typia.assert(updated);
  TestValidator.equals("status after update")(newStatus)(updated.status);
  TestValidator.equals("published_at after update")(newPublishedAt)(updated.published_at);

  // 3. Attempt to update immutable fields (should reject changes to id or generated_at)
  const attemptedImmutableUpdate: IComplianceReport.IUpdate = {
    // @ts-expect-error (for the sake of test, but should cause server-side rejection)
    id: "11111111-1111-1111-1111-111111111111",
    // @ts-expect-error
    report_type: "INVALID_UPDATE",
    // @ts-expect-error
    generated_at: new Date().toISOString(),
    status: "superseded",
  };
  await TestValidator.httpError("immutable field update should fail")(400)(() =>
    api.functional.securityCompliance.complianceReport.putById(
      connection,
      { id: report.id, body: attemptedImmutableUpdate as any }
    )
  );

  // 4. Attempt update with invalid data (bad published_at format)
  const badUpdate: IComplianceReport.IUpdate = {
    status: "superseded",
    // @ts-expect-error
    published_at: "not-a-date",
  };
  await TestValidator.httpError("invalid date for published_at should be rejected")(400)(() =>
    api.functional.securityCompliance.complianceReport.putById(
      connection,
      { id: report.id, body: badUpdate as any }
    )
  );

  // 5. Attempt to update a nonexistent report ID
  await TestValidator.httpError("update on nonexistent id should fail")(404)(() =>
    api.functional.securityCompliance.complianceReport.putById(
      connection,
      {
        id: "00000000-0000-0000-0000-000000000000",
        body: updateBody
      }
    )
  );
}

