 *
 * 1. Search by specific organization_id (resulting in one or multiple policies).
 * 2. Filter by entity_type to show only policies for a certain entity (e.g., 'user').
 * 3. Filter where is_legal_hold is true (legal hold policies only).
 * 4. Request with no filters (all policies paged).
 * 5. Validate pagination: fetch multiple pages, check boundary behavior (first and last page), totals, and empty result (page beyond last).
 * 6. Confirm API response contains proper pagination info and expected IDataRetention.ISummary structures.
 */
export async function test_api_data_retention_list_search_filter_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Search by organization_id
  const orgId = "123e4567-e89b-12d3-a456-426614174000";
  const orgFiltered: IPageIDataRetention.ISummary = await api.functional.securityCompliance.dataRetention.patch(connection, {
    body: {
      organization_id: orgId,
    } satisfies IDataRetention.IRequest,
  });
  typia.assert(orgFiltered);
  orgFiltered.data.forEach((item) => {
    if (item.organization_id)
      TestValidator.equals("organization_id matches")(orgId)(item.organization_id);
  });

  // 2. Filter by entity_type
  const entityType = "user";
  const typeFiltered: IPageIDataRetention.ISummary = await api.functional.securityCompliance.dataRetention.patch(connection, {
    body: {
      entity_type: entityType,
    } satisfies IDataRetention.IRequest,
  });
  typia.assert(typeFiltered);
  typeFiltered.data.forEach((item) =>
    TestValidator.equals("entity_type matches")(entityType)(item.entity_type)
  );

  // 3. Filter by is_legal_hold
  const legalHoldFiltered: IPageIDataRetention.ISummary = await api.functional.securityCompliance.dataRetention.patch(connection, {
    body: {
      is_legal_hold: true,
    } satisfies IDataRetention.IRequest,
  });
  typia.assert(legalHoldFiltered);
  legalHoldFiltered.data.forEach((item) =>
    TestValidator.equals("is_legal_hold true")(true)(item.is_legal_hold)
  );

  // 4. Request with no filters (all policies paged)
  const allPolicies: IPageIDataRetention.ISummary = await api.functional.securityCompliance.dataRetention.patch(connection, {
    body: {} satisfies IDataRetention.IRequest,
  });
  typia.assert(allPolicies);
  TestValidator.equals("data array should not be null or undefined")(true)(Array.isArray(allPolicies.data));

  // 5. Pagination test: fetch first, then next/last page and then out-of-bound
  const pageLimit = 2;
  const page1: IPageIDataRetention.ISummary = await api.functional.securityCompliance.dataRetention.patch(connection, {
    body: {
      // Suppose 'current' and 'limit' are recognized as paging parameters on BE system, though not explicit in DTO. Adjust if explicit.
      current: 1,
      limit: pageLimit,
    } as any,
  });
  typia.assert(page1);
  TestValidator.equals("pagination.current == 1")(1)(page1.pagination.current);
  TestValidator.equals("page limitation")(pageLimit)(page1.pagination.limit);

  // Next page
  if (page1.pagination.pages > 1) {
    const page2: IPageIDataRetention.ISummary = await api.functional.securityCompliance.dataRetention.patch(connection, {
      body: {
        current: 2,
        limit: pageLimit,
      } as any,
    });
    typia.assert(page2);
    TestValidator.equals("pagination.current == 2")(2)(page2.pagination.current);
    // Should not repeat data
    TestValidator.equals("no overlap between page1 and page2")(
      0,
    )(
      page1.data.filter(item1 =>
        page2.data.find(item2 => item2.id === item1.id)
      ).length
    );
  }

  // Fetch out-of-bounds page (should be empty data)
  const outOfBoundsPage: IPageIDataRetention.ISummary = await api.functional.securityCompliance.dataRetention.patch(connection, {
    body: {
      current: 9999,
      limit: pageLimit,
    } as any,
  });
  typia.assert(outOfBoundsPage);
  TestValidator.equals("empty page at out-of-bound")([])(outOfBoundsPage.data);

  // 6. Confirm each record structure and sorting/order if relevant
  allPolicies.data.forEach((item) => {
    typia.assert(item); // Must match IDataRetention.ISummary
    TestValidator.equals("has id")(true)(!!item.id);
    TestValidator.equals("entity_type string")(true)(typeof item.entity_type === "string");
    TestValidator.equals("retention_period_days is int")(
      Math.floor(item.retention_period_days)
    )(item.retention_period_days);
    TestValidator.equals("created_at date-time")(true)(typeof item.created_at === "string");
  });
}

