import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IEvidenceArchive } from "@ORGANIZATION/PROJECT-api/lib/structures/IEvidenceArchive";
import { IPageIEvidenceArchive } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIEvidenceArchive";

/**
 * Test searching and paginating through evidence archive records with various filters.
 *
 * This test covers evidence archive listing for regulatory and audit access scenarios,
 * verifying that pagination, filtering and access controls work properly.
 *
 * 1. Query for a specific evidence_type and verify only matching types are in results.
 * 2. Paginate through multiple pages, checking page count and result data quantity.
 * 3. Test filter by a partial checksum (integrity hash) and creation time range.
 * 4. Attempt a search with filter parameters that yield empty results (no records).
 * 5. Attempt edge pagination (first and last page) and validate meta info.
 * 6. Check that unauthorized access to archives (if supported by system) is blocked appropriately and sensitive data is not leaked.
 */
export async function test_api_evidence_archive_list_with_pagination_filter_and_authz(
  connection: api.IConnection,
): Promise<void> {
  // 1. Query for a specific evidence_type
  const filterType = "GDPR_DSAR_EXPORT";
  const firstPage: IPageIEvidenceArchive = await api.functional.securityCompliance.evidenceArchive.patch(connection, {
    body: {
      evidence_type: filterType,
      page: 1,
      limit: 5,
    } satisfies IEvidenceArchive.IRequest,
  });
  typia.assert(firstPage);
  // Assert all returned evidence types match filter
  for (const record of firstPage.data) {
    TestValidator.equals("evidence_type must match filter")(filterType)(record.evidence_type);
  }
  // Store for later use
  const totalPages = firstPage.pagination.pages;
  const totalRecords = firstPage.pagination.records;
  // If there are enough records, check multiple pages
  if (totalPages > 1) {
    const secondPage: IPageIEvidenceArchive = await api.functional.securityCompliance.evidenceArchive.patch(connection, {
      body: {
        evidence_type: filterType,
        page: 2,
        limit: 5,
      } satisfies IEvidenceArchive.IRequest,
    });
    typia.assert(secondPage);
    TestValidator.equals("should have next page")(2)(secondPage.pagination.current);
    // No overlap in results between pages
    const page1Ids = new Set(firstPage.data.map(r => r.id));
    for (const r of secondPage.data) {
      TestValidator.equals("no overlap between page 1 and 2")(false)(page1Ids.has(r.id));
    }
  }

  // 2. Filter by a partial checksum and created_at range
  if (firstPage.data.length > 0) {
    const partialChecksum = firstPage.data[0].checksum.slice(0, 6);
    const createdAt = firstPage.data[0].created_at;
    const filterCheck: IPageIEvidenceArchive = await api.functional.securityCompliance.evidenceArchive.patch(connection, {
      body: {
        checksum: partialChecksum, // If API supports partial, otherwise use full value
        created_at_from: createdAt,
        created_at_to: createdAt,
        page: 1,
        limit: 5,
      } satisfies IEvidenceArchive.IRequest,
    });
    typia.assert(filterCheck);
    for (const rec of filterCheck.data) {
      TestValidator.equals("checksum contains search fragment")(true)(rec.checksum.includes(partialChecksum));
      TestValidator.equals("created_at matches")(createdAt)(rec.created_at);
    }
  }

  // 3. Search with parameters that should yield no records
  const emptyResult: IPageIEvidenceArchive = await api.functional.securityCompliance.evidenceArchive.patch(connection, {
    body: {
      evidence_type: "NON_EXISTENT_TYPE_" + Date.now(),
      page: 1,
      limit: 3,
    } satisfies IEvidenceArchive.IRequest,
  });
  typia.assert(emptyResult);
  TestValidator.equals("data should be empty")([])(emptyResult.data);

  // 4. Edge pagination: last page
  if (totalPages > 1) {
    const lastPage: IPageIEvidenceArchive = await api.functional.securityCompliance.evidenceArchive.patch(connection, {
      body: {
        evidence_type: filterType,
        page: totalPages,
        limit: 5,
      } satisfies IEvidenceArchive.IRequest,
    });
    typia.assert(lastPage);
    TestValidator.equals("is last page")(totalPages)(lastPage.pagination.current);
    // Page count is correct
    TestValidator.equals("page count matches meta info")(totalPages)(lastPage.pagination.pages);
  }

  // 5. Authorization test: try to access as unauthorized user if infra supports (simulate if needed)
  // This step assumes that changing the connection can simulate lower privileges
  // (In actual implementation, replace with real unauthorized connection or session)
  if (connection.headers?.authorization) {
    const lowAuthConnection = { ...connection, headers: { ...connection.headers, authorization: "Bearer INVALID_TOKEN" } };
    await TestValidator.httpError("unauthorized evidence archive access")(401)(() =>
      api.functional.securityCompliance.evidenceArchive.patch(lowAuthConnection, {
        body: {
          evidence_type: filterType,
          page: 1,
          limit: 1,
        } satisfies IEvidenceArchive.IRequest,
      }),
    );
  }
}

