export async function test_api_evidence_archive_create_required_and_unique(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a valid evidence archive record
  const createBody: IEvidenceArchive.ICreate = {
    evidence_type: "GDPR_EXPORT",
    blob_url: "https://bucket.s3.region.amazonaws.com/evidence/file1.bin",
