  TestValidator.equals("blob_url")(createBody.blob_url)(output.blob_url);
  TestValidator.equals("size_bytes")(createBody.size_bytes)(output.size_bytes);
  TestValidator.equals("checksum")(createBody.checksum)(output.checksum);
  TestValidator.equals("id is uuid")(true)(/^[0-9a-f\-]{36}$/i.test(output.id));

