import api from "@ORGANIZATION/PROJECT-api";
import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import { IPageIApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIApiClients";

/**
 * Test listing/searching API clients with filtering and pagination.
 *
 * This function verifies core listing and searching features for API clients, ensuring search and pagination options work and edge cases are handled.
 *
 * 1. List all clients with default pagination.
 * 2. Search clients by name (expecting at least one match).
 * 3. Filter only active or inactive clients.
 * 4. Request page size limit (limit=1) and paginate to the next page.
 * 5. Request an excessive page number (expecting empty set).
 * 6. If there are results, request the last valid page and check data shape.
 */
export async function test_api_api_client_list_filtering_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. List all API clients using default pagination (no filters)
  const all: IPageIApiClients = await api.functional.apiOmnichannel.apiClients.patch(connection, {
    body: {},
  });
  typia.assert(all);
  TestValidator.equals("pagination present")(typeof all.pagination === "object")(true);
  TestValidator.equals("array data")(Array.isArray(all.data))(true);

  // 2. If any client exists, search by the first client's name
  if (all.data.length > 0) {
    const anyClient: IApiClients = all.data[0];
    const byName: IPageIApiClients = await api.functional.apiOmnichannel.apiClients.patch(connection, {
      body: { client_name: anyClient.client_name },
    });
    typia.assert(byName);
    TestValidator.equals("search by client_name finds ID")(byName.data.some(c => c.id === anyClient.id))(true);
  }

  // 3. Filter by is_active status (if both kinds exist)
  const hasActive = all.data.some(c => c.is_active);
  const hasInactive = all.data.some(c => !c.is_active);
  if (hasActive) {
    const activePage: IPageIApiClients = await api.functional.apiOmnichannel.apiClients.patch(connection, {
      body: { is_active: true },
    });
    typia.assert(activePage);
    TestValidator.equals("active only")(activePage.data.every(c => c.is_active))(true);
  }
  if (hasInactive) {
    const inactivePage: IPageIApiClients = await api.functional.apiOmnichannel.apiClients.patch(connection, {
      body: { is_active: false },
    });
    typia.assert(inactivePage);
    TestValidator.equals("inactive only")(inactivePage.data.every(c => !c.is_active))(true);
  }

  // 4. Use pagination: limit=1
  const firstPage: IPageIApiClients = await api.functional.apiOmnichannel.apiClients.patch(connection, {
    body: { limit: 1, page: 1 },
  });
  typia.assert(firstPage);
  TestValidator.equals("first page one record")(firstPage.data.length <= 1)(true);
  // If there is more, request the second page
  if (firstPage.pagination.pages > 1) {
    const secondPage: IPageIApiClients = await api.functional.apiOmnichannel.apiClients.patch(connection, {
      body: { limit: 1, page: 2 },
    });
    typia.assert(secondPage);
    TestValidator.equals("second page one record")(secondPage.data.length <= 1)(true);
  }

  // 5. Excessively high page number (expect empty array)
  const excessivePage: IPageIApiClients = await api.functional.apiOmnichannel.apiClients.patch(connection, {
    body: { page: 9999, limit: 1 },
  });
  typia.assert(excessivePage);
  TestValidator.equals("excess page data")(excessivePage.data.length)(0);

  // 6. Confirm last page structure matches expectations, if clients exist
  if (all.pagination.pages > 0 && all.pagination.pages !== 1) {
    const lastPage: IPageIApiClients = await api.functional.apiOmnichannel.apiClients.patch(connection, {
      body: { page: all.pagination.pages, limit: all.pagination.limit },
    });
    typia.assert(lastPage);
    TestValidator.equals("last page number")(lastPage.pagination.current)(all.pagination.pages);
    TestValidator.equals("last page array")(Array.isArray(lastPage.data))(true);
  }
}

