import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IFavoritesPersonalization } from "@ORGANIZATION/PROJECT-api/lib/structures/IFavoritesPersonalization";

/**
 * Validate paginated, filtered, and sorted retrieval of personalization events.
 *
 * This test:
 * - Seeds diverse personalization events so that filtering, pagination, and sorting can be verified.
 * - Ensures that each filter (user_id, event_type, date range, etc.) and pagination combination returns correct results and metadata.
 * - Tests empty result and invalid filter scenarios for correct API behavior.
 *
 * 1. Create several personalization events (diverse user_id, event_type, timestamps).
 * 2. Query with no filter for all events.
 * 3. Filter by user_id and event_type, check that only those events are returned.
 * 4. Filter with date ranges, validate correct events are included/excluded.
 * 5. Use pagination (limit/page) and check correct slicing and meta.
 * 6. Test sort ordering.
 * 7. Query with filters that return empty data.
 * 8. Query with invalid filters to ensure errors raised.
 */
export async function test_api_favorites_personalization_list_with_pagination_and_filtering(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: Seed multiple events with unique combinations
  const baseTime = new Date();
  const testUsers = [typia.random<string>(), typia.random<string>()];
  const testTypes = ["view", "click", "save", "dismiss"];
  const testEvents: IFavoritesPersonalization.IPersonalizationEvent[] = [];
  for (let i = 0; i < 7; ++i) {
    const event = await api.functional.favoritesPersonalization.personalizationEvents.post(
      connection,
      {
        body: {
          user_id: testUsers[i % 2],
          recommendation_id: typia.random<string>(),
          product_id: typia.random<string>(),
          event_type: testTypes[i % 4],
          model_version: "v1-" + i,
          context: i % 2 === 0 ? "test-context" : undefined,
        } satisfies IFavoritesPersonalization.IPersonalizationEventCreate,
      }
    );
    typia.assert(event);
    testEvents.push(event);
  }
  // Sort testEvents by timestamp (descending) for cross-check
  testEvents.sort((a, b) => b.timestamp.localeCompare(a.timestamp));

  // Step 2: Query with no filters, just default pagination
  const listAll = await api.functional.favoritesPersonalization.personalizationEvents.patch(
    connection,
    { body: {} },
  );
  typia.assert(listAll);
  // Validate all seeded events exist in the returned data
  for (const created of testEvents) {
    const match = listAll.data.find(x => x.id === created.id);
    TestValidator.equals("seeded event present")(!!created.id)(!!match?.id);
  }

  // Step 3: Filter by user_id and event_type
  const filterUser = testEvents[1].user_id;
  const filterType = testEvents[1].event_type;
  const filtered = await api.functional.favoritesPersonalization.personalizationEvents.patch(
    connection,
    {
      body: {
        user_id: filterUser,
        event_type: filterType,
      },
    },
  );
  typia.assert(filtered);
  for (const ev of filtered.data) {
    TestValidator.equals("filtered user_id")(filterUser)(ev.user_id);
    TestValidator.equals("filtered event_type")(filterType)(ev.event_type);
  }

  // Step 4: Time range filters
  const from = testEvents[0].timestamp;
  const to = testEvents[3].timestamp;
  const inRange = await api.functional.favoritesPersonalization.personalizationEvents.patch(
    connection,
    {
      body: {
        timestamp_from: from,
        timestamp_to: to,
      },
    },
  );
  typia.assert(inRange);
  // Check all returned events are within the range
  for (const ev of inRange.data) {
    TestValidator.equals("in range timestamp")(true)(ev.timestamp >= from && ev.timestamp <= to);
  }

  // Step 5: Pagination (page/limit) tests
  const pageLimit = 3;
  const page2 = await api.functional.favoritesPersonalization.personalizationEvents.patch(
    connection,
    {
      body: {
        limit: pageLimit,
        page: 2,
      },
    },
  );
  typia.assert(page2);
  TestValidator.equals("pagination page")(2)(page2.pagination.current);
  TestValidator.equals("pagination limit")(pageLimit)(page2.pagination.limit);
  // The returned data should be <= pageLimit
  TestValidator.equals("pagination batch size")(true)(page2.data.length <= pageLimit);

  // Step 6: Sorting test
  const sortedDesc = await api.functional.favoritesPersonalization.personalizationEvents.patch(
    connection,
    {
      body: {
        sort: "timestamp:desc",
      },
    },
  );
  typia.assert(sortedDesc);
  let prev = "9999-99-99T99:99:99Z";
  for (const ev of sortedDesc.data) {
    TestValidator.equals("sorted desc")(true)(ev.timestamp <= prev);
    prev = ev.timestamp;
  }

  // Step 7: Filter yields empty result
  const emptyList = await api.functional.favoritesPersonalization.personalizationEvents.patch(
    connection,
    {
      body: {
        user_id: "00000000-0000-0000-0000-000000000000",
        event_type: "no-such-type",
      },
    },
  );
  typia.assert(emptyList);
  TestValidator.equals("empty results")(0)(emptyList.data.length);

  // Step 8: Invalid filter/test error handling (invalid UUID, negative limit)
  await TestValidator.httpError("invalid UUID")(400)(() =>
    api.functional.favoritesPersonalization.personalizationEvents.patch(
      connection,
      { body: { user_id: "invalid-uuid" } },
    ),
  );
  await TestValidator.httpError("negative page")(400)(() =>
    api.functional.favoritesPersonalization.personalizationEvents.patch(
      connection,
      { body: { page: -1 } },
    ),
  );
  await TestValidator.httpError("limit zero")(400)(() =>
    api.functional.favoritesPersonalization.personalizationEvents.patch(
      connection,
      { body: { limit: 0 } },
    ),
  );
}

