    const rec: IRecommendation = await api.functional.favoritesPersonalization.recommendations.post(connection, {
      body: {
        user_id: userIds[i % userIds.length] as any,
        product_id: productIds[i % productIds.length] as any,
        model_id: modelIds[i % modelIds.length],
        model_version: modelVersions[i % modelVersions.length],
        rationale: i % 2 === 0 ? `reason_${i}` : null,
        recommendation_time: new Date(Date.now() - i * 3600_000).toISOString(),
      } satisfies IRecommendation.ICreate,
    });
    typia.assert(rec);
    recommendations.push(rec);
  }

  // 2. Search by user_id filter
  for (const userId of userIds) {
    const page: IPageIRecommendation = await api.functional.favoritesPersonalization.recommendations.patch(connection, {
      body: { user_id: userId } satisfies IRecommendation.IRequest,
    });
    typia.assert(page);
    for (const rec of page.data) TestValidator.equals("user_id")(userId)(rec.user_id);
  }

  // 3. Search by user_id + model_id
  const filterUser = userIds[0];
  const filterModel = modelIds[0];
  const pageUserModel: IPageIRecommendation = await api.functional.favoritesPersonalization.recommendations.patch(connection, {
    body: { user_id: filterUser, model_id: filterModel } satisfies IRecommendation.IRequest,
  });
  typia.assert(pageUserModel);
  for (const rec of pageUserModel.data) {
    TestValidator.equals("user_id")(filterUser)(rec.user_id);
    TestValidator.equals("model_id")(filterModel)(rec.model_id);
  }

  // 4. Search using recommendation_time_from/recommendation_time_to
  const fromTime = recommendations[2].recommendation_time;
  const toTime = recommendations[5].recommendation_time;
  const pageByTime: IPageIRecommendation = await api.functional.favoritesPersonalization.recommendations.patch(connection, {
    body: {
      recommendation_time_from: fromTime,
      recommendation_time_to: toTime,
    } satisfies IRecommendation.IRequest,
  });
  typia.assert(pageByTime);
  for (const rec of pageByTime.data) {
    TestValidator.equals("after from_time")(
      true
    )(
      rec.recommendation_time >= fromTime
    );
    TestValidator.equals("before to_time")(
      true
    )(
      rec.recommendation_time <= toTime
    );
  }

  // 5. Pagination: limit & offset
  const limit = 3;
  const pagePagination: IPageIRecommendation = await api.functional.favoritesPersonalization.recommendations.patch(connection, {
    body: { limit } satisfies IRecommendation.IRequest,
  });
  typia.assert(pagePagination);
  TestValidator.equals("page data length")(limit)(pagePagination.data.length);

  // 6. Pagination: boundary condition (past end)
  const offset = 20;
  const pagePastEnd: IPageIRecommendation = await api.functional.favoritesPersonalization.recommendations.patch(connection, {
    body: { offset } satisfies IRecommendation.IRequest,
  });
  typia.assert(pagePastEnd);
  TestValidator.equals("empty past-end page")(0)(pagePastEnd.data.length);

  // 7. No match filter
  const pageNoMatch: IPageIRecommendation = await api.functional.favoritesPersonalization.recommendations.patch(connection, {
    body: {
      user_id: "00000000-0000-0000-0000-000000000000",
      model_id: "never-exists-model",
    } satisfies IRecommendation.IRequest,
  });
  typia.assert(pageNoMatch);
  TestValidator.equals("empty no-match page")(0)(pageNoMatch.data.length);

  // 8. Invalid filter: invalid user_id format (should be handled gracefully)
  const pageInvalid: IPageIRecommendation = await api.functional.favoritesPersonalization.recommendations.patch(connection, {
    body: {
      user_id: "INVALID_UUID",
    } satisfies IRecommendation.IRequest,
  });
  typia.assert(pageInvalid);
}

