import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IRecommendation } from "@ORGANIZATION/PROJECT-api/lib/structures/IRecommendation";

/**
 * E2E test for updating recommendation records via PUT /favoritesPersonalization/recommendations/:id.
 *
 * Scenario:
 * 1. Create a new recommendation record (POST).
 * 2. Update allowed mutable fields (model_version, rationale, recommendation_time) and verify the changes persisted.
 * 3. Attempt to update non-existent ID and confirm not found error is returned.
 * 4. Attempt to change immutable/system fields (user_id, product_id, model_id, id) and confirm update is rejected or those fields are unchanged.
 */
export async function test_api_recommendation_update_by_id_with_valid_and_invalid_scenarios(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a recommendation record for testing updates
  const createBody: IRecommendation.ICreate = {
    user_id: typia.random<string>(),
    product_id: typia.random<string>(),
    model_id: "test-model-v1",
    model_version: "1.0.0",
    rationale: "Initial rationale.",
    recommendation_time: new Date().toISOString(),
  };
  const original: IRecommendation = await api.functional.favoritesPersonalization.recommendations.post(connection, { body: createBody });
  typia.assert(original);

  // 2. Update allowed mutable fields and verify changes
  const updateBody: IRecommendation.IUpdate = {
    model_version: "2.0.0",
    rationale: "Updated rationale.",
    recommendation_time: new Date(Date.now() + 1000 * 60 * 60).toISOString(),
  };
  const updated: IRecommendation = await api.functional.favoritesPersonalization.recommendations.putById(connection, {
    id: original.id,
    body: updateBody,
  });
  typia.assert(updated);
  TestValidator.equals("model_version updated")("2.0.0")(updated.model_version);
  TestValidator.equals("rationale updated")("Updated rationale.")(updated.rationale);
  TestValidator.equals("recommendation_time updated")(updateBody.recommendation_time)(updated.recommendation_time);
  TestValidator.equals("user_id unchanged")(original.user_id)(updated.user_id);
  TestValidator.equals("product_id unchanged")(original.product_id)(updated.product_id);
  TestValidator.equals("model_id unchanged")(original.model_id)(updated.model_id);

  // 3. Attempt to update non-existent ID and expect error (404 or relevant)
  await TestValidator.httpError("recommendation not found")(404)(() =>
    api.functional.favoritesPersonalization.recommendations.putById(connection, {
      id: typia.random<string>(),
      body: updateBody,
    }),
  );

  // 4. Attempt to update immutable/system fields (simulate via any-casting)
  // (should not change on server, or update should error out)
  const illegalUpdate: any = {
    model_version: "3.0.0",
    user_id: typia.random<string>(),
    product_id: typia.random<string>(),
    model_id: "SHOULD-NOT-CHANGE",
  };
  const result: IRecommendation = await api.functional.favoritesPersonalization.recommendations.putById(connection, {
    id: original.id,
    body: illegalUpdate as IRecommendation.IUpdate,
  });
  typia.assert(result);
  // The allowed field gets updated, others stay the same
  TestValidator.equals("model_version updated illegal")("3.0.0")(result.model_version);
  TestValidator.equals("user_id remains unchanged even if attempted illegal mutation")(original.user_id)(result.user_id);
  TestValidator.equals("product_id remains unchanged even if attempted illegal mutation")(original.product_id)(result.product_id);
  TestValidator.equals("model_id remains unchanged even if attempted illegal mutation")(original.model_id)(result.model_id);
}

import { TestValidator } from "@nestia/e2e";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IRecommendation } from "@ORGANIZATION/PROJECT-api/lib/structures/IRecommendation";

/**
 * Test soft-deleting a recommendation by ID, list/get exclusion, and redundant deletion error handling.
 *
 * This scenario verifies that a recommendation can be soft-deleted by its ID and after deletion:
 * - The record is not returned by the list or get endpoints as active (if list/get endpoints were defined)
 * - The audit/history chain is still preserved for compliance (manually check returned deleted record if supported)
 * - Redundant deletion returns a handled error instead of success
 *
 * Steps:
 * 1. Create a recommendation (to guarantee the ID exists for deletion)
 * 2. Soft-delete the recommendation by its ID
 * 3. Try deleting the same recommendation again (should return an error or appropriate failure response)
 * 4. Try deleting a non-existent recommendation ID (should return an error)
 */
export async function test_api_recommendation_soft_delete_and_redundant_delete_handling(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a recommendation
  const recommendation: IRecommendation = await api.functional.favoritesPersonalization.recommendations.post(
    connection,
    {
      body: {
        user_id: typia.random<string & tags.Format<"uuid">>(),
        product_id: typia.random<string & tags.Format<"uuid">>(),
        model_id: "gpt-4-recommender-A",
        model_version: "1.0.2",
        rationale: "test rationale for deletion flows",
        recommendation_time: new Date().toISOString(),
      } satisfies IRecommendation.ICreate,
    },
  );
  typia.assert(recommendation);

  // 2. Soft-delete the recommendation by ID
  const deleted: IRecommendation = await api.functional.favoritesPersonalization.recommendations.eraseById(
    connection,
    { id: recommendation.id },
  );
  typia.assert(deleted);
  TestValidator.equals("deleted record id should match input id")(recommendation.id)(deleted.id);

  // 3. Try deleting the same recommendation again (should fail)
  await TestValidator.error("Redundant deletion returns error")(() =>
    api.functional.favoritesPersonalization.recommendations.eraseById(
      connection,
      { id: recommendation.id },
    ),
  );

  // 4. Try deleting a non-existent recommendation ID (should fail)
  await TestValidator.error("Deleting non-existent recommendation returns error")(() =>
    api.functional.favoritesPersonalization.recommendations.eraseById(
      connection,
      { id: typia.random<string & tags.Format<"uuid">>() },
    ),
  );
}

import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IPageIPersonalizationAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIPersonalizationAudit";
import { IPersonalizationAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IPersonalizationAudit";

/**
 * List/search personalization audit records with pagination and various filter options.
 *
 * This test covers:
 * - Creation of multiple audit records with diverse properties
 * - Searching using filters (user_id, action_type, date range, evidence_hash)
 * - Pagination (fetching first/last page, boundary checks)
 * - Edge case handling: empty dataset, no filter match, invalid filters, and very large dataset
 *   for performance/robustness
 *
 * Scenario:
 * 1. Create multiple audit records (various user_ids, action_types, timestamps, hashes)
 * 2. List audits with no filter, confirm pagination (count, pages, records).
 * 3. List with filter by user_id. Only those matching user.
 * 4. List with filter by action_type. Only the right event types returned.
 * 5. Filter by timestamp_from and timestamp_to, check range results.
 * 6. Filter by evidence_hash (single, nonmatch, boundary/no-match)
 * 7. Edge: Use a filter that matches no audit. Should return empty set with correct page info.
 * 8. Edge: Use invalid filter value (e.g. bad UUID in user_id)expect error or empty.
 * 9. Create enough audits to test pagination (>limit records; limit=3). Check first and last page.
 */
export async function test_api_personalization_audit_list_filter_pagination_edges(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create diverse audit records
  const now = new Date();
  const users = Array(3)
    .fill(0)
    .map(() => typia.random<string & typia.tags.Format<"uuid">>());
  const action_types = ["ADD_FAVORITE", "REMOVE_BOOKMARK", "ACTIONS"];
  const timestamps: string[] = [
    new Date(now.getTime() - 1000 * 3600 * 24 * 5).toISOString(), // -5 days
    new Date(now.getTime() - 1000 * 3600 * 24 * 2).toISOString(), // -2 days
    now.toISOString(),
  ];
  const evidence_hashes = [
    "hashA__" + Math.random(),
    "hashB__" + Math.random(),
    "hashC__" + Math.random(),
  ];

  const created: IPersonalizationAudit[] = [];
  for (let i = 0; i < 7; ++i) {
    const audit: IPersonalizationAudit = await api.functional.favoritesPersonalization.personalizationAudits.post(
      connection,
      {
        body: {
          user_id: users[i % users.length],
          action_type: action_types[i % action_types.length],
          evidence_hash: evidence_hashes[i % evidence_hashes.length],
          timestamp: timestamps[i % timestamps.length],
        },
      } as any,
    );
    typia.assert(audit);
    created.push(audit);
  }
  // Additional audits for pagination (>limit, limit=3 for test)
  for (let i = 0; i < 5; ++i) {
    const audit: IPersonalizationAudit = await api.functional.favoritesPersonalization.personalizationAudits.post(
      connection,
      {
        body: {
          user_id: users[0],
          action_type: "ADD_FAVORITE",
          evidence_hash: "plimit_" + i,
          timestamp: new Date(now.getTime() - i * 1000 * 60 * 30).toISOString(),
        },
      } as any,
    );
    typia.assert(audit);
    created.push(audit);
  }
  // 2. List all audits (no filter)
  let res: IPageIPersonalizationAudit =
    await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, {
      body: {},
    });
  typia.assert(res);
  TestValidator.equals("total count")(created.length)(res.pagination.records);
  TestValidator.equals("data count")(res.data.length <= res.pagination.limit)(true);

  // 3. List with user_id filter
  res = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, {
    body: { user_id: users[1] },
  });
  typia.assert(res);
  TestValidator.equals("user_id filtered")(res.data.every(r => r.user_id === users[1]))(true);

  // 4. action_type filter
  res = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, {
    body: { action_type: "REMOVE_BOOKMARK" },
  });
  typia.assert(res);
  TestValidator.equals("action_type filtered")(res.data.every(r => r.action_type === "REMOVE_BOOKMARK"))(true);

  // 5. timestamp range
  const from = timestamps[0], to = timestamps[1];
  res = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, {
    body: { timestamp_from: from, timestamp_to: to },
  });
  typia.assert(res);
  TestValidator.equals("timestamp range")(res.data.every(r => r.timestamp >= from && r.timestamp <= to))(true);

  // 6. evidence_hash
  res = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, {
    body: { evidence_hash: evidence_hashes[1] },
  });
  typia.assert(res);
  TestValidator.equals("evidence_hash filtered")(res.data.every(r => r.evidence_hash === evidence_hashes[1]))(true);
  // no-match case
  res = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, {
    body: { evidence_hash: "definitely-not-a-real-hash" },
  });
  typia.assert(res);
  TestValidator.equals("empty result for unknown hash")(res.data.length)(0);

  // 7. Edge: filter matches no audit (impossible user_id)
  res = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, {
    body: { user_id: "00000000-0000-0000-0000-000000000000" },
  });
  typia.assert(res);
  TestValidator.equals("empty user id")(res.data.length)(0);

  // 8. Edge: invalid filter (bad UUID)
  await TestValidator.error("bad uuid")(() =>
    api.functional.favoritesPersonalization.personalizationAudits.patch(connection, {
      body: { user_id: "not-a-uuid" as any },
    })
  );

  // 9. Pagination. Use user[0], limit 3
  res = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, {
    body: { user_id: users[0], limit: 3, offset: 0 },
  });
  typia.assert(res);
  TestValidator.equals("pagination limit")(res.data.length <= 3)(true);
  const pageCount = res.pagination.pages;
  TestValidator.equals("page 1 data present")(res.data.length > 0)(true);
  // fetch last page
  if (pageCount > 1) {
    res = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, {
      body: { user_id: users[0], limit: 3, offset: (pageCount - 1) * 3 },
    });
    typia.assert(res);
    TestValidator.equals("last page ok")(res.pagination.current)(pageCount);
  }
}

