/**
 * Ensure correct update behavior of personalization audit records by ID,
 * including data integrity, immutable field protections, and correct error response for non-existent records.
 *
 * 1. Create an audit record to enable update scenario testing.
 * 2. Update the audit record with valid fields; verify changes are reflected and non-updated fields remain unchanged.
 * 3. Attempt to update a non-existent record and check for proper error response (404).
 * 4. Attempt to "update" immutable fields (id, user_id) and ensure they are not changed.
 */
export async function test_api_personalization_audit_update_integrity(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create an initial personalization audit
  const createInput: IPersonalizationAudit.ICreate = {
    user_id: typia.random<string>(),
    favorite_id: undefined,
    bookmark_id: undefined,
    recommendation_id: undefined,
    personalization_event_id: undefined,
    action_type: "INITIAL",
    evidence_hash: "original_hash_12345",
    timestamp: new Date().toISOString(),
  };
  const created: IPersonalizationAudit = await api.functional.favoritesPersonalization.personalizationAudits.post(
    connection,
    { body: createInput }
  );
  typia.assert(created);

  // 2. Update the audit with valid changes (e.g. action_type, evidence_hash)
  const updateInput: IPersonalizationAudit.IUpdate = {
    action_type: "AMENDED",
    evidence_hash: "updated_hash_67890",
  };
  const updated: IPersonalizationAudit = await api.functional.favoritesPersonalization.personalizationAudits.putById(
    connection,
    {
      id: created.id,
      body: updateInput
    },
  );
  typia.assert(updated);
  TestValidator.equals("update.action_type")("AMENDED")(updated.action_type);
  TestValidator.equals("update.evidence_hash")("updated_hash_67890")(updated.evidence_hash);
  TestValidator.equals("immutable.id")(created.id)(updated.id);
  TestValidator.equals("immutable.user_id")(created.user_id)(updated.user_id);

  // 3. Attempt to update a non-existent record ID (expect 404 Not Found or equivalent error)
  await TestValidator.httpError("not found")(404)(() =>
    api.functional.favoritesPersonalization.personalizationAudits.putById(
      connection,
      {
        id: typia.random<string>(),
        body: updateInput,
      },
    ),
  );

  // 4. Attempt to update immutable fields: id and user_id
  const illegalUpdateInput: IPersonalizationAudit.IUpdate = {
    // @ts-expect-error: Intentionally trying illegal updates, but will strip these when passed to API
    id: typia.random<string>(),
    // @ts-expect-error
    user_id: typia.random<string>(),
    action_type: "SHOULD_IGNORE",
    evidence_hash: "malicious_change",
  };
  const result: IPersonalizationAudit = await api.functional.favoritesPersonalization.personalizationAudits.putById(
    connection,
    {
      id: created.id,
      // Only the valid update fields should be sent (illegal fields should be ignored)
      body: {
        action_type: "SHOULD_IGNORE",
        evidence_hash: "malicious_change",
      }
    },
  );
  typia.assert(result);
  // The id and user_id must not change
  TestValidator.equals("id not changed")(created.id)(result.id);
  TestValidator.equals("user_id not changed")(created.user_id)(result.user_id);
  // If system ignores illegal input, action_type/evidence_hash can update
  TestValidator.equals("action_type after illegal input")("SHOULD_IGNORE")(result.action_type);
  TestValidator.equals("evidence_hash after illegal input")("malicious_change")(result.evidence_hash);
}

