/**
 * List/search AI model versions by filtering and pagination edge cases.
 *
 * This test ensures that the listing API for AI model versions behaves correctly
 * under a variety of filtering and paging scenarios, including empty queries,
 * no-matching/failing filters, overloaded datasets, and proper subset queries.
 * The procedure includes setting up representative AI model versions to cover edge
 * and normal cases, then running multiple combinations of PATCH queries
 * accordingly.
 *
 * 1. Create several AI model versions with distinct ai_model_id, version, and framework combinations, some deployed, some not.
 * 2. List all versions without filters. Check full set.
 * 3. Filter by ai_model_id and verify only relevant versions returned.
 * 4. Filter by version (partial/substring) and validate matching logic.
 * 5. Filter by deployed (true/false) and verify correct results for each.
 * 6. Combine filters (e.g., ai_model_id + deployed), confirm strict AND logic.
 * 7. Test for filters with no matches  expect empty result.
 * 8. Paginate result set (limit/page), confirm correctness and total/offset.
 *
 * Verifies that all API-side filtering, searching, and pagination logic is robust and correct.
 */
export async function test_list_ai_model_versions_with_search_and_filter_edge_cases(
  connection: api.IConnection,
): Promise<void> {
  // 1. Prepare distinct ai_model_ids for edge & normal cases
  const aiModelIdA = typia.random<string & typia.tags.Format<"uuid">>();
  const aiModelIdB = typia.random<string & typia.tags.Format<"uuid">>();
  const versionList = ["v1.0.0", "v2.0.1-beta", "test", "release", "vX.999"];
  const frameworks = ["PyTorch", "TensorFlow", "scikit-learn"];
  const now = new Date();
  const makeDate = (offsetDays: number) => new Date(now.getTime() + 86400000 * offsetDays).toISOString();
  // 1.1. Bulk-create versions for both model ids, frameworks, and various dates
  const createdVersions: IAiModelVersion[] = [];
  for (const ai_model_id of [aiModelIdA, aiModelIdB]) {
    for (let i = 0; i < versionList.length; ++i) {
      const model: IAiModelVersion = await api.functional.advancedAI.aiModelVersions.post(connection, {
        body: {
          ai_model_id,
          version: versionList[i],
          version_hash: typia.random<string>(),
          artifact_url: `s3://bucket/model/${ai_model_id}/${versionList[i]}`,
          framework: frameworks[i % frameworks.length],
          metadata: JSON.stringify({ test: true, tag: versionList[i] }),
        } satisfies IAiModelVersion.ICreate,
      });
      typia.assert(model);
      createdVersions.push(model);
    }
  }

  // 2. List all without filter
  const allRes: IPageIAiModelVersion = await api.functional.advancedAI.aiModelVersions.patch(connection, {
    body: {} satisfies IAiModelVersion.IRequest,
  });
  typia.assert(allRes);
  const foundIds = new Set(allRes.data.map(x => x.id));
  for (const v of createdVersions) TestValidator.equals("full set present")(true)(foundIds.has(v.id));

  // 3. Filter by ai_model_id only
  const filteredA = await api.functional.advancedAI.aiModelVersions.patch(connection, {
    body: { ai_model_id: aiModelIdA } satisfies IAiModelVersion.IRequest,
  });
  typia.assert(filteredA);
  for (const rec of filteredA.data) TestValidator.equals("ai_model_id A match")(aiModelIdA)(rec.ai_model_id);
  TestValidator.equals("ai_model_id A size")(versionList.length)(filteredA.data.length);

  // 4. Filter by version substring
  const filteredTest = await api.functional.advancedAI.aiModelVersions.patch(connection, {
    body: { version: "test" } satisfies IAiModelVersion.IRequest,
  });
  typia.assert(filteredTest);
  TestValidator.equals("version substring match")(true)(filteredTest.data.some(x => x.version.includes("test")));

  // 5. Filter by not deployed (should be all, as none have deployed_at)
  const filteredNotDeployed = await api.functional.advancedAI.aiModelVersions.patch(connection, {
    body: { deployed: false } satisfies IAiModelVersion.IRequest,
  });
  typia.assert(filteredNotDeployed);
  TestValidator.equals("not deployed count")(createdVersions.length)(filteredNotDeployed.data.length);

  // 6. Filter by ai_model_id + version
  const filteredBoth = await api.functional.advancedAI.aiModelVersions.patch(connection, {
    body: { ai_model_id: aiModelIdB, version: versionList[0] } satisfies IAiModelVersion.IRequest,
  });
  typia.assert(filteredBoth);
  TestValidator.equals("ai_model_id + version")(1)(filteredBoth.data.length);
  TestValidator.equals("ai_model_id + version values")(versionList[0])(filteredBoth.data[0]?.version);
  TestValidator.equals("ai_model_id + version values")(aiModelIdB)(filteredBoth.data[0]?.ai_model_id);

  // 7. No match filter
  const noMatch = await api.functional.advancedAI.aiModelVersions.patch(connection, {
    body: { ai_model_id: typia.random<string & typia.tags.Format<"uuid">>() } satisfies IAiModelVersion.IRequest,
  });
  typia.assert(noMatch);
  TestValidator.equals("no match empty")(0)(noMatch.data.length);

  // 8. Test page/limit
  const pageSize = 3;
  const paginated = await api.functional.advancedAI.aiModelVersions.patch(connection, {
    body: {
      ai_model_id: aiModelIdA,
      // Limit/pagination typically handled inside props.body; adapt as needed
      limit: pageSize,
      current: 1,
    } as any,
  });
  typia.assert(paginated);
  TestValidator.equals("pagination limit")(pageSize)(paginated.data.length);
  TestValidator.equals("pagination total")(versionList.length)(paginated.pagination.records);
}

