import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IAiModelVersion } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiModelVersion";

/**
 * Create a new AI model version and validate all relevant scenarios.
 *
 * This test covers multiple aspects of model version creation:
 * - Normal creation of an AI model version with all fields specified.
 * - Validation checks for missing required fields (should fail).
 * - Validation for invalid data types or formats (should fail appropriately).
 * - Attempting to create another model version with a conflicting model/version/hash combination, confirming unique constraints and proper error handling.
 *
 * Steps:
 * 1. Create a valid AI model version (with all fields set, including optional metadata).
 * 2. Attempt creation missing required fields (expect error).
 * 3. Attempt creation with invalid field types/structures (expect error).
 * 4. Attempt creation of a version with the same ai_model_id and version fields (conflict; expect error).
 */
export async function test_api_ai_model_version_create_with_full_validation_logic(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a valid AI model version (all fields set, including metadata)
  const validPayload: IAiModelVersion.ICreate = {
    ai_model_id: typia.random<string & typia.tags.Format<"uuid">>(),
    version: "v1.0.0",
    version_hash: "abcdef1234567890hashvalueforuniqueness1234",
    artifact_url: "s3://my-ml-artifact-bucket/model_v1.0.0.bin",
    framework: "PyTorch",
    metadata: JSON.stringify({ description: "First deployment of v1.0.0", params: { lr: 0.001, epochs: 10 } }),
  };
  const createdVersion: IAiModelVersion = await api.functional.advancedAI.aiModelVersions.post(connection, { body: validPayload });
  typia.assert(createdVersion);
  TestValidator.equals("ai_model_id")(validPayload.ai_model_id)(createdVersion.ai_model_id);
  TestValidator.equals("version")(validPayload.version)(createdVersion.version);
  TestValidator.equals("version_hash")(validPayload.version_hash)(createdVersion.version_hash);
  TestValidator.equals("artifact_url")(validPayload.artifact_url)(createdVersion.artifact_url);
  TestValidator.equals("framework")(validPayload.framework)(createdVersion.framework);
  TestValidator.equals("metadata")(validPayload.metadata)(createdVersion.metadata);

  // 2. Attempt creation missing required fields (expect error)
  await TestValidator.error("missing required field ai_model_id")(() =>
    // @ts-expect-error
    api.functional.advancedAI.aiModelVersions.post(connection, {
      body: {
        ...validPayload,
        ai_model_id: undefined,
      } as any,
    })
  );
  await TestValidator.error("missing required field version")(() =>
    // @ts-expect-error
    api.functional.advancedAI.aiModelVersions.post(connection, {
      body: {
        ...validPayload,
        version: undefined,
      } as any,
    })
  );
  await TestValidator.error("missing required field artifact_url")(() =>
    // @ts-expect-error
    api.functional.advancedAI.aiModelVersions.post(connection, {
      body: {
        ...validPayload,
        artifact_url: undefined,
      } as any,
    })
  );

  // 3. Attempt creation with invalid field types/structures (expect error)
  await TestValidator.error("invalid UUID format for ai_model_id")(() =>
    // @ts-expect-error
    api.functional.advancedAI.aiModelVersions.post(connection, {
      body: {
        ...validPayload,
        ai_model_id: "not-a-uuid",
      } as any,
    })
  );
  await TestValidator.error("metadata not a JSON string")(() =>
    // @ts-expect-error
    api.functional.advancedAI.aiModelVersions.post(connection, {
      body: {
        ...validPayload,
        metadata: { isJson: true },
      } as any,
    })
  );

  // 4. Attempt creation of a version with conflicting ai_model_id & version (unique constraint; expect error)
  await TestValidator.error("conflict on ai_model_id+version")(() =>
    api.functional.advancedAI.aiModelVersions.post(connection, {
      body: { ...validPayload },
    }),
  );
}

import { TestValidator } from "@nestia/e2e";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
