/**
 * Delete an AI model version by ID and verify soft-delete and audit retention.
 *
 * Scenario:
 * 1. Create an AI model version (to ensure a test subject for delete).
 * 2. Perform soft-delete using eraseById endpoint.
 * 3. Assert the IIdDeleted response including status and deleted_at fields.
 * 4. Attempt to delete the same ID again (expect error or already deleted behavior).
 * 5. Attempt to delete a random non-existent UUID (expect error response).
 * 6. [Optional in system] Query soft-deleted ID for compliance audit if API allows.
 *
 * The purpose is to verify deletion does not entirely destroy record (for compliance) but disables it from active operations, and errors are handled correctly for repeat/nonexistent deletes.
 */
export async function test_api_ai_model_version_delete_soft_and_audit(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a test AI model version
  const modelVer: IAiModelVersion = await api.functional.advancedAI.aiModelVersions.post(connection, {
    body: {
      ai_model_id: typia.random<string & typia.tags.Format<"uuid">>(),
      version: `vtest-${Date.now()}`,
      version_hash: typia.random<string>(),
      artifact_url: "s3://bucket/test-artifact.pt",
      framework: "PyTorch",
      metadata: JSON.stringify({ creator: "tester", notes: "delete scenario" })
    } satisfies IAiModelVersion.ICreate
  });
  typia.assert(modelVer);

  // 2. Delete by ID
  const delResult: ICommon.IIdDeleted = await api.functional.advancedAI.aiModelVersions.eraseById(connection, {
    id: modelVer.id,
  });
  typia.assert(delResult);
  TestValidator.equals("AI model version deleted id")(modelVer.id)(delResult.id);
  TestValidator.equals("AI model version deleted status")("deleted")(delResult.status.toLowerCase());
  TestValidator.check("deleted_at should be defined")(!!delResult.deleted_at);

  // 3. Try to delete again (should error or return already deleted)
  await TestValidator.error("delete already deleted AI model version")(() =>
    api.functional.advancedAI.aiModelVersions.eraseById(connection, { id: modelVer.id })
  );

  // 4. Try to delete a non-existent UUID
  await TestValidator.error("delete non-existent AI model version")(() =>
    api.functional.advancedAI.aiModelVersions.eraseById(connection, { id: typia.random<string & typia.tags.Format<"uuid">>() })
  );

  // Optional: If audit endpoints exist, invoke here to ensure audit data retained.
}

import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IAiTrainingData } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiTrainingData";
import { IAiTrainingData } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiTrainingData";
import { IPageIAiTrainingData } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIAiTrainingData";

/**
 * List and search AI training data registries with advanced filtering and permissions.
 *
 * This test will:
 * 1. Create multiple AI training dataset entries (with variation in name, model association, version).
 * 2. List and search datasets by name (exact/partial matching), model association, and using empty or overloaded filters.
 * 3. Test paginated queries (page, limit) and boundary pages (empty results, last page).
 * 4. Test incorrect, non-matching, or illogical filters and ensure empty/valid responses.
 * 5. Confirm user permission edge cases (requires setup, but here we check general endpoint acceptance).
 *
 * These operations ensure the advancedAI/aiTrainingData PATCH endpoint supports all legal filtering and pagination combinations, responds with accurate page/data metadata, and robustly handles edge and error cases.
 */
export async function test_api_ai_training_data_list_search_filter_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create multiple AI training datasets with various properties
  const created: IAiTrainingData[] = [];
  for (let i = 0; i < 5; ++i) {
    const entry: IAiTrainingData = await api.functional.advancedAI.aiTrainingData.post(connection, {
      body: {
        name: `regression-dataset-${i}`,
        version: `v1.${i}`,
        source_uri: `s3://bucket/path/regression-dataset-${i}.csv`,
        schema_hash: `hash${100+i}`,
        ai_model_id: i % 2 === 0 ? null : undefined, // alternate null/undefined
        record_count: 100 + i * 10,
      } satisfies IAiTrainingData.ICreate,
    });
    typia.assert(entry);
    created.push(entry);
  }
  // Additional: with different model
  const withAIModel: IAiTrainingData = await api.functional.advancedAI.aiTrainingData.post(connection, {
    body: {
      name: "classification-dataset",
      version: "v2",
      source_uri: "s3://bucket/path/classification-dataset-v2.csv",
      schema_hash: "hash-class-v2",
      ai_model_id: "a1e99730-1f99-416c-a836-fb5dc54d77a5", // fixed UUID for repeatable filter
      record_count: 500,
    } satisfies IAiTrainingData.ICreate,
  });
  typia.assert(withAIModel);
  created.push(withAIModel);

  // 2. Query by name: exact match
  let resp = await api.functional.advancedAI.aiTrainingData.patch(connection, {
    body: { name: "classification-dataset" },
  });
  typia.assert(resp);
  TestValidator.equals("name-exact-match count")(1)(resp.data.length);
  TestValidator.equals("classification-dataset name")(withAIModel.name)(resp.data[0].name);

  // 3. Query by partial name
  resp = await api.functional.advancedAI.aiTrainingData.patch(connection, {
    body: { name: "regression" },
  });
  typia.assert(resp);
  TestValidator.equals("partial-regression-match count")(5)(resp.data.length);
  resp.data.forEach(e => TestValidator.equals("partial-name-contains-regression")(true)(e.name.includes("regression")));

  // 4. Query by ai_model_id
  resp = await api.functional.advancedAI.aiTrainingData.patch(connection, {
    body: { ai_model_id: "a1e99730-1f99-416c-a836-fb5dc54d77a5" },
  });
  typia.assert(resp);
  TestValidator.equals("ai_model_id count")(1)(resp.data.length);
  TestValidator.equals("ai_model_id match")(withAIModel.ai_model_id)(resp.data[0].ai_model_id);

  // 5. Pagination - limit 2 per page, check all pages
  resp = await api.functional.advancedAI.aiTrainingData.patch(connection, {
    body: { limit: 2, page: 1 },
  });
  typia.assert(resp);
  TestValidator.equals("page 1 size")(2)(resp.data.length);
  resp = await api.functional.advancedAI.aiTrainingData.patch(connection, {
    body: { limit: 2, page: 2 },
  });
  typia.assert(resp);
  TestValidator.equals("page 2 size")(2)(resp.data.length);
  resp = await api.functional.advancedAI.aiTrainingData.patch(connection, {
    body: { limit: 2, page: 3 },
  });
  typia.assert(resp);
  TestValidator.equals("page 3 size")(created.length - 4)(resp.data.length); // last page

  // 6. Overloaded filter (non-existent name)
  resp = await api.functional.advancedAI.aiTrainingData.patch(connection, {
    body: { name: "impossible-name-xyz" },
  });
  typia.assert(resp);
  TestValidator.equals("no results")(0)(resp.data.length);

  // 7. Illogical filters (created_from after created_to)
  const future = new Date(Date.now() + 100000).toISOString();
  const past = new Date(Date.now() - 100000).toISOString();
  resp = await api.functional.advancedAI.aiTrainingData.patch(connection, {
    body: { created_from: future, created_to: past },
  });
  typia.assert(resp);
  TestValidator.equals("illogical-date")(0)(resp.data.length);

  // 8. Empty filter (should return default page)
  resp = await api.functional.advancedAI.aiTrainingData.patch(connection, {
    body: {},
  });
  typia.assert(resp);
  TestValidator.equals("full list count")(created.length)(resp.data.length);

  // 9. Boundaries: negative page/limit (invalid input; system should default or clamp)
  resp = await api.functional.advancedAI.aiTrainingData.patch(connection, {
    body: { page: -1, limit: -10 },
  });
  typia.assert(resp);
  // Accept either full or default, not error
  TestValidator.equals("negative page/limit handled")(created.length)(resp.data.length);
}

