  typia.assert(updatedCampaign);
  TestValidator.equals("updated campaign name")(newName)(updatedCampaign.name);
  TestValidator.equals("updated active status")(false)(updatedCampaign.active);

  // 4. Try to update with end_at before start_at (business invalid case)
  const invalidUpdate: IDiscountCampaign.IUpdate = {
    start_at: new Date(now.getTime() + 30_000).toISOString(),
    end_at: new Date(now.getTime() + 10_000).toISOString(), // End before start
  };
  await TestValidator.error("invalid date range should fail")(
    () => api.functional.paymentsDiscounts.discountCampaigns.putById(connection, {
      id: initialCampaign.id,
      body: invalidUpdate,
    }),
  );

  // 5. Attempt to update campaign name to a duplicate
  const anotherCampaign: IDiscountCampaign = await api.functional.paymentsDiscounts.discountCampaigns.post(connection, {
    body: {
      name: `SuperSale_Duplicate_${now.getTime()}`,
      description: "Another campaign for duplicate name test.",
      start_at: new Date(now.getTime() + 50_000).toISOString(),
      end_at: new Date(now.getTime() + 150_000).toISOString(),
      active: true,
    } satisfies IDiscountCampaign.ICreate,
  });
  typia.assert(anotherCampaign);
  await TestValidator.error("duplicate campaign name should fail")(
    () => api.functional.paymentsDiscounts.discountCampaigns.putById(connection, {
      id: anotherCampaign.id,
      body: { name: newName },
    }),
  );

  // 6. Attempt to update a non-existent campaign (using random UUID)
  await TestValidator.httpError("update non-existent campaign should return 404")(404)(
    () => api.functional.paymentsDiscounts.discountCampaigns.putById(connection, {
      id: "00000000-0000-0000-0000-000000000000" as string & tags.Format<"uuid">,
      body: { name: "Ghost campaign" },
    }),
  );
}

