import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IPageITransaction } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageITransaction";
import { ITransaction } from "@ORGANIZATION/PROJECT-api/lib/structures/ITransaction";

/**
 * Retrieve a paginated and filterable list of transactions.
 *
 * This test validates the transaction search API by supplying combinations of filters (status/type, date range, user) and verifying that results are paginated and filtered correctly.
 * Also performs negative tests for invalid filter parameters to confirm correct error handling.
 *
 * 1. Query all transactions with no filters (default pagination)
 * 2. Query transactions with specific user_id filter
 * 3. Query transactions within a specific date range
 * 4. Query transactions by type filter (e.g., purchase, refund, etc.)
 * 5. Query with pagination (page & limit), verify correct slicing of data
 * 6. Attempt to query with invalid filter parameters (e.g., malformed UUIDs, negative page/limit) and expect error response
 */
export async function test_api_transaction_list_with_advanced_filters(
  connection: api.IConnection,
): Promise<void> {
  // 1. Query all transactions (no filters)
  let output: IPageITransaction = await api.functional.paymentsDiscounts.transactions.patch(connection, {
    body: {},
  });
  typia.assert(output);
  TestValidator.equals("default page current")(1)(output.pagination.current);
  TestValidator.equals("default page limit")(output.data.length)(output.data.length);

  // Prepare a user_id for filtering (use first transaction if present)
  const hasData = output.data.length > 0;
  const sampleUserId = hasData ? output.data[0].user_id : undefined;

  // 2. Query transactions filtered by user_id
  if (sampleUserId) {
    const filteredByUser = await api.functional.paymentsDiscounts.transactions.patch(connection, {
      body: { user_id: sampleUserId },
    });
    typia.assert(filteredByUser);
    TestValidator.equals("user_id filter")(sampleUserId)(
      filteredByUser.data[0]?.user_id,
    );
  }

  // 3. Query transactions in a date range (pick dates from sample if available)
  if (hasData) {
    const created = output.data[0].created_at;
    const createdBefore = new Date(new Date(created).getTime() + 1000).toISOString();
    const filteredByDate = await api.functional.paymentsDiscounts.transactions.patch(connection, {
      body: { created_after: created, created_before: createdBefore },
    });
    typia.assert(filteredByDate);
    for (const tx of filteredByDate.data) {
      TestValidator.equals("created_after filter")(true)(tx.created_at >= created);
      TestValidator.equals("created_before filter")(true)(tx.created_at <= createdBefore);
    }
  }

  // 4. Query by type filter (using type from sample data if present)
  if (hasData) {
    const sampleType = output.data[0].type;
    const filteredByType = await api.functional.paymentsDiscounts.transactions.patch(connection, {
      body: { type: sampleType },
    });
    typia.assert(filteredByType);
    for (const tx of filteredByType.data) {
      TestValidator.equals("type filter")(sampleType)(tx.type);
    }
  }

  // 5. Pagination: query with limit 2, page 2, and check correct data slicing
  const paginated = await api.functional.paymentsDiscounts.transactions.patch(connection, {
    body: { limit: 2, page: 2 },
  });
  typia.assert(paginated);
  TestValidator.equals("pagination limit")(2)(paginated.pagination.limit);
  TestValidator.equals("pagination current page")(2)(paginated.pagination.current);

  // 6. Negative testing: invalid parameters
  // 6.1 Invalid user_id (malformed UUID)
  await TestValidator.httpError("invalid user_id uuid")(400)(() =>
    api.functional.paymentsDiscounts.transactions.patch(connection, {
      body: { user_id: "invalid-uuid" as any },
    })
  );

  // 6.2 Negative page number
  await TestValidator.httpError("negative page")(400)(() =>
    api.functional.paymentsDiscounts.transactions.patch(connection, {
      body: { page: -1 as any },
    })
  );

  // 6.3 Negative limit
  await TestValidator.httpError("negative limit")(400)(() =>
    api.functional.paymentsDiscounts.transactions.patch(connection, {
      body: { limit: -5 as any },
    })
  );
}

