import api from "@ORGANIZATION/PROJECT-api";
import { ITransaction } from "@ORGANIZATION/PROJECT-api/lib/structures/ITransaction";

/**
 * Validate update operation on a transaction entity.
 *
 * This E2E test covers the following scenarios for paymentsDiscounts.transactions update:
 * 1. Create a valid transaction (prerequisite).
 * 2. Update permitted fields (description/type/order_id) and verify changes.
 * 3. Attempt to update immutable/restricted fields (id/user_id/created_at) and expect update failure (validation or no effect).
 * 4. Try updating a non-existent transaction and expect a 404 error.
 */
export async function test_api_transaction_update_with_validation_and_edge_cases(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a valid transaction
  const initial: ITransaction = await api.functional.paymentsDiscounts.transactions.post(connection, {
    body: {
      user_id: typia.random<typeof initial.user_id>(),
      type: "purchase",
      created_at: new Date().toISOString(),
      description: "Original description",
    } satisfies ITransaction.ICreate,
  });
  typia.assert(initial);

  // 2. Valid update: modify 'description', add or modify 'order_id', change 'type'.
  const newDescription = "Updated for correction by audit";
  const newType = "refund";
  const newOrderId = typia.random<typeof initial.user_id>();
  const updated: ITransaction = await api.functional.paymentsDiscounts.transactions.putById(connection, {
    id: initial.id,
    body: {
      description: newDescription,
      type: newType,
      order_id: newOrderId,
    } satisfies ITransaction.IUpdate,
  });
  typia.assert(updated);
  TestValidator.equals("id should remain unchanged")(initial.id)(updated.id);
  TestValidator.equals("user_id should remain unchanged")(initial.user_id)(updated.user_id);
  TestValidator.equals("updated description")(newDescription)(updated.description);
  TestValidator.equals("updated type")(newType)(updated.type);
  TestValidator.equals("updated order_id")(newOrderId)(updated.order_id);
  TestValidator.equals("created_at is preserved")(initial.created_at)(updated.created_at);

  // 3. Attempt to update forbidden/immutable fields and expect error or ignored changes
  // Try to set 'id', 'user_id', and 'created_at' via update - these must not change
  // We'll try to pass them in body (even though schema should not allow);
  // in runtime test, inject extra keys and expect validation error (if tolerated) or no change
  const forbiddenPatch: any = {
    id: typia.random<typeof initial.id>(),
    user_id: typia.random<typeof initial.user_id>(),
    created_at: new Date("2000-01-01T00:00:00.000Z").toISOString(),
    description: "Should not be settable",
  };
  await TestValidator.httpError("Attempt update on forbidden/immutable fields")(400)(() =>
    api.functional.paymentsDiscounts.transactions.putById(connection, {
      id: initial.id,
      body: forbiddenPatch,
    })
  );

  // 4. Attempt to update a non-existent transaction, expect 404
  await TestValidator.httpError("Update non-existent transaction")(404)(() =>
    api.functional.paymentsDiscounts.transactions.putById(connection, {
      id: typia.random<string>(), // random UUID, not in DB
      body: { description: "Should not exist" } satisfies ITransaction.IUpdate,
    })
  );
}

