/**
 * List and search vouchers using multiple filters and pagination.
 *
 * This test ensures that the voucher listing endpoint supports multi-criteria search
 * and pagination, and validates both result structure and expected query/filter
 * behaviors. The test exercises filters by user, campaign, status, pagination controls,
 * and checks for correct error handling with invalid parameters.
 *
 * Steps:
 * 1. Request all vouchers with default (no filter) parameters, verify shape.
 * 2. Filter by a valid user_id and check the returned vouchers belong to that user.
 * 3. Filter by a campaign_id and check campaign correspondence.
 * 4. Filter by redeemed = true, all vouchers should have a non-null redeemed_at.
 * 5. Combine user_id + redeemed = false. Should only return vouchers for that user and not redeemed.
 * 6. Paginate (e.g., limit=2), verify total counts and pagination logic.
 * 7. Send invalid page parameter and expect error/validation failure.
 */
export async function test_api_voucher_list_and_search_with_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: All vouchers (no filters)
  const all: IPageIVoucher = await api.functional.paymentsDiscounts.vouchers.patch(connection, { body: {} });
  typia.assert(all);
  TestValidator.equals("pagination.record-count")(typeof all.pagination.records)("number");

  // Step 2: Filter by existing user_id (pick from actual data)
  const testUserId = all.data[0]?.user_id || "00000000-0000-4000-8000-000000000001";
  const userFiltered: IPageIVoucher = await api.functional.paymentsDiscounts.vouchers.patch(connection, { body: { user_id: testUserId } });
  typia.assert(userFiltered);
  for (const v of userFiltered.data) TestValidator.equals("user_id filter")(testUserId)(v.user_id);

  // Step 3: Filter by campaign_id
  const testCampaignId = all.data.find((v) => v.campaign_id)?.campaign_id || "00000000-0000-4000-8000-000000000002";
  const campaignFiltered: IPageIVoucher = await api.functional.paymentsDiscounts.vouchers.patch(connection, { body: { campaign_id: testCampaignId } });
  typia.assert(campaignFiltered);
  for (const v of campaignFiltered.data) TestValidator.equals("campaign_id filter")(testCampaignId)(v.campaign_id);

  // Step 4: Filter by redeemed=true
  const redeemedFiltered: IPageIVoucher = await api.functional.paymentsDiscounts.vouchers.patch(connection, { body: { redeemed: true } });
  typia.assert(redeemedFiltered);
  for (const v of redeemedFiltered.data) TestValidator.equals("redeemed filter")(true)(!!v.redeemed_at);

  // Step 5: Combined user_id + redeemed=false
  const combined: IPageIVoucher = await api.functional.paymentsDiscounts.vouchers.patch(connection, { body: { user_id: testUserId, redeemed: false } });
  typia.assert(combined);
