  if (fullList.data.some((d) => d.type)) {
    const sampleType = fullList.data.find((d) => !!d.type)?.type ?? "earn";
    const byType: IPageILoyaltyPoint = await api.functional.paymentsDiscounts.loyaltyPoints.patch(connection, {
      body: { type: sampleType },
    });
    typia.assert(byType);
    for (const row of byType.data) {
      TestValidator.equals("type matches filter")(row.type)(sampleType);
    }
