  // 5. Filter positive points only (if any positive entries found)
  if (fullList.data.some((d) => d.points > 0)) {
    const byPositive: IPageILoyaltyPoint = await api.functional.paymentsDiscounts.loyaltyPoints.patch(connection, {
      body: { type: "earn" },
    });
    typia.assert(byPositive);
    for (const row of byPositive.data) {
      TestValidator.equals("points positive")(row.points > 0)(true);
    }
  }

  // 6. Test pagination (limit 2)
  const page2: IPageILoyaltyPoint = await api.functional.paymentsDiscounts.loyaltyPoints.patch(connection, {
    body: { limit: 2, page: 2 },
  });
  typia.assert(page2);
  TestValidator.equals("pagination.limit")(page2.pagination.limit)(2);
  TestValidator.equals("pagination.current")(page2.pagination.current)(2);

  // 7. Invalid filter (malformed user_id UUID)
  await TestValidator.httpError("bad filter")(400)(() =>
    api.functional.paymentsDiscounts.loyaltyPoints.patch(connection, {
      body: { user_id: "not-a-uuid" as any },
    }),
  );
}

