import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { ICoreConsent } from "@ORGANIZATION/PROJECT-api/lib/structures/ICoreConsent";
import { IPageICoreConsent } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICoreConsent";

/**
 * Search and paginate through the list of user consents, confirming that filtering and sorting parameters work as expected.
 *
 * This test validates all key aspects of the Core.consents PATCH endpoint's search and pagination:
 *  - Ensures that filtering and sorting by user_id, type, and grant status operate correctly.
 *  - Handles edge cases: no results found, large result sets, and invalid filter and sort values.
 *
 * Steps:
 * 1. Create multiple ICoreConsent entries with varying user_id, type, is_granted states.
 * 2. Search/patch with no filtersverify paginated list returns expected entries and metadata.
 * 3. Filter by specific user_id, type, and is_granted and check results are correct.
 * 4. Apply sorting (e.g., by granted_at:desc) and verify order.
 * 5. Paginate with limit/page parametersconfirm page size and navigation.
 * 6. Test edge case: filter that should return no results.
 * 7. Test edge case: very large page limit.
 * 8. Test edge case: supply invalid filter values and check error behavior or empty results.
 */
export async function test_api_consent_search_and_paginate_consents_with_various_filters(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create multiple ICoreConsent entries
  const users = [
    typia.random<string & typia.tags.Format<"uuid">>(),
    typia.random<string & typia.tags.Format<"uuid">>(),
  ];
  const now = new Date();
  const types = ["ai_training", "marketing", "analytics"];
  const created: ICoreConsent[] = [];
  for (let u of users) {
    for (let t of types) {
      for (let granted of [true, false]) {
        const entry: ICoreConsent = await api.functional.core.consents.post(connection, {
          body: {
            user_id: u,
            type: t,
            is_granted: granted,
            granted_at: now.toISOString(),
            revoked_at: granted ? null : now.toISOString(),
          } satisfies ICoreConsent.ICreate,
        });
        typia.assert(entry);
        created.push(entry);
      }
    }
  }

  // 2. No filter: Should return all consents
  let res: IPageICoreConsent = await api.functional.core.consents.patch(connection, {
    body: {} satisfies ICoreConsent.IRequest,
  });
  typia.assert(res);
  // Should contain all we created
  for (const consent of created) {
    TestValidator.equals(`created consent exists: ${consent.id}`)(true)(res.data.some(c => c.id === consent.id));
  }

  // 3. Filter by user_id, type, and is_granted
  {
    const filter: any = { user_id: users[0], type: types[0], is_granted: true };
    res = await api.functional.core.consents.patch(connection, {
      body: {
        filter,
      } satisfies ICoreConsent.IRequest,
    });
    typia.assert(res);
    for (const consent of res.data) {
      TestValidator.equals("user_id match")(users[0])(consent.user_id);
      TestValidator.equals("type match")(types[0])(consent.type);
      TestValidator.equals("is_granted true")(true)(consent.is_granted);
    }
  }

  // 4. Sorting by granted_at DESC
  {
    const sort = "granted_at:desc";
    res = await api.functional.core.consents.patch(connection, {
      body: { sort },
    });
    typia.assert(res);
    for (let i = 1; i < res.data.length; ++i) {
      TestValidator.equals("granted_at descending order")(
        true,
      )(
        res.data[i - 1].granted_at >= res.data[i].granted_at,
      );
    }
  }

  // 5. Pagination test: limit=2, page=2
  {
    res = await api.functional.core.consents.patch(connection, {
      body: { limit: 2, page: 2 } satisfies ICoreConsent.IRequest,
    });
    typia.assert(res);
    TestValidator.equals("limit is 2")(2)(res.pagination.limit);
    TestValidator.equals("current page is 2")(2)(res.pagination.current);
    TestValidator.equals("pages count")(
      Math.ceil(res.pagination.records / res.pagination.limit),
    )(res.pagination.pages);
  }

  // 6. Edge: Filter with impossible value (no matches)
  {
    res = await api.functional.core.consents.patch(connection, {
      body: { filter: { type: "nonexistent_type" } } satisfies ICoreConsent.IRequest,
    });
    typia.assert(res);
    TestValidator.equals("no data for nonexistent type")(0)(res.data.length);
  }

  // 7. Edge: Unusually large limit
  {
    res = await api.functional.core.consents.patch(connection, {
      body: { limit: 1000 } satisfies ICoreConsent.IRequest,
    });
    typia.assert(res);
    TestValidator.equals("limit is 1000")(1000)(res.pagination.limit);
    // Should not fail, may be capped by server
  }

  // 8. Edge: Invalid filter (unsupported field)
  {
    try {
      await api.functional.core.consents.patch(connection, {
        body: { filter: { _invalid_field_: "xxx" } } as any, // purposely invalid
      });
      // If no error, consider it compliant with graceful handling (backend may just ignore unknown field)
    } catch (err) {
      TestValidator.error("invalid filter triggers error")(err);
    }
  }
}

