/**
 * E2E test for creating a user regulatory consent (Core Consent).
 *
 * This test covers creation of a consent entity with valid data, validation for missing/invalid fields,
 * and ensures duplicate consent entries (for same user + type) are rejected. Fulfills compliance
 * requirements and validates error handling for edge cases.
 *
 * 1. Successfully create a consent for a user with valid data
 * 2. Attempt to create consent with missing mandatory fields (should fail validation)
 * 3. Attempt to create consent with invalid data types (should fail validation)
 * 4. Attempt to create a duplicate consent for the same user & type (should fail uniqueness rule)
 */
export async function test_api_consent_create_and_validate(
  connection: api.IConnection,
): Promise<void> {
  // Reusable user_id
  const user_id = typia.random<string & typia.tags.Format<"uuid">>();
  const baseBody: ICoreConsent.ICreate = {
    user_id,
    type: "ai_training",
    is_granted: true,
    granted_at: new Date().toISOString(),
  };

  // 1. Create consent with valid data
  const consent: ICoreConsent = await api.functional.core.consents.post(connection, {
    body: baseBody,
  });
  typia.assert(consent);
  TestValidator.equals("user_id")(baseBody.user_id)(consent.user_id);
  TestValidator.equals("type")(baseBody.type)(consent.type);
  TestValidator.equals("is_granted")(baseBody.is_granted)(consent.is_granted);
  TestValidator.equals("granted_at")(baseBody.granted_at)(consent.granted_at);

  // 2. Missing field (type) - should fail validation
  await TestValidator.httpError("missing mandatory field: type")(422)(() =>
    api.functional.core.consents.post(connection, {
      body: { ...baseBody, type: undefined as any },
    }),
  );

  // 3. Invalid type (is_granted as string instead of boolean) - should fail validation
  await TestValidator.httpError("invalid field type: is_granted")(422)(() =>
    api.functional.core.consents.post(connection, {
      body: { ...baseBody, is_granted: "yes" as any },
    }),
  );

  // 4. Duplicate consent for same user_id & type - should fail uniqueness rule
  await TestValidator.httpError("duplicate consent entry")(409)(() =>
    api.functional.core.consents.post(connection, {
      body: baseBody,
    }),
  );
}

import { TestValidator } from "@nestia/e2e";
