/**
 * Update a consent entry by its ID and check that the updates are correctly saved.
 * Attempt to update with incomplete or invalid data to verify validation logic and error responses.
 * Test updating a non-existent consent entry returns an appropriate error.
 *
 * Steps:
 * 1. Create a valid consent record using the POST /core/consents endpoint
 * 2. Update the consent entry by its ID with a valid update (PUT /core/consents/{id}); verify the updates are reflected.
 * 3. Attempt to update the created consent record with invalid or incomplete data; verify a validation error is returned.
 * 4. Attempt to update a non-existent consent id; verify 'not found' or corresponding error is returned.
 */
export async function test_api_core_consent_update_with_validation_and_not_found(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a valid consent record
  const createInput: ICoreConsent.ICreate = {
    user_id: typia.random<string & typia.tags.Format<"uuid">>(),
    type: "ai_training",
    is_granted: true,
    granted_at: new Date().toISOString(),
    revoked_at: null,
  };
  const created: ICoreConsent = await api.functional.core.consents.post(
    connection,
    { body: createInput }
  );
  typia.assert(created);

  // 2. Update the consent entry (success case)
  const updateBody: ICoreConsent.IUpdate = {
    type: "marketing",
    is_granted: false,
    granted_at: created.granted_at,
    revoked_at: new Date().toISOString(),
  };
  const updated: ICoreConsent = await api.functional.core.consents.putById(
    connection,
    {
      id: created.id,
      body: updateBody,
    }
  );
  typia.assert(updated);
  TestValidator.equals("id should remain same")(created.id)(updated.id);
  TestValidator.equals("user_id should remain same")(created.user_id)(updated.user_id);
  TestValidator.equals("type updated")("marketing")(updated.type);
  TestValidator.equals("is_granted updated")(false)(updated.is_granted);
  TestValidator.equals("revoked_at updated")(updateBody.revoked_at)(updated.revoked_at);

  // 3. Try to update with invalid data (empty type, missing values)
  await TestValidator.httpError("should fail on empty update body")(422)(() =>
    api.functional.core.consents.putById(connection, {
      id: created.id,
      body: {}, // completely empty update should not be valid
    })
  );

  await TestValidator.httpError("should fail on wrong type value")(422)(() =>
    api.functional.core.consents.putById(connection, {
      id: created.id,
      body: {
        type: "", // empty string is likely invalid for business rule
      },
    })
  );

  // 4. Try to update a non-existent consent entry
  await TestValidator.httpError("not found for non-existent id")(404)(() =>
    api.functional.core.consents.putById(connection, {
      id: typia.random<string & typia.tags.Format<"uuid">>(),
      body: { is_granted: false }
    })
  );
}

