import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { ICoreAccessLog } from "@ORGANIZATION/PROJECT-api/lib/structures/ICoreAccessLog";
import { IPageICoreAccessLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICoreAccessLog";

/**
 * List access logs with various filters, paginations, and sorting options to validate data retrieval.
 *
 * This test validates listing and query features for access logs, covering filters, pagination, edge, and error cases.
 *
 * 1. Insert diverse access log records using POST /core/accessLogs (at least 2 different user_ids).
 * 2. PATCH /core/accessLogs with no filters: verify all logs are returned and paginated correctly.
 * 3. PATCH /core/accessLogs with user_id, event_type, ip, and compound filter: verify results match filter.
 * 4. Use page and limit pagination and verify pagination metadata/data correctness.
 * 5. Use occurred_at_from and occurred_at_to for time range query; validate proper filtering.
 * 6. (If supported) verify deterministic/sorted output.
 * 7. Try filter with non-existing user_id and expect an empty data array.
 * 8. Try invalid/malformed filter (bad types) and expect error response.
 */
export async function test_list_access_logs_with_filter_and_pagination_options(connection: api.IConnection): Promise<void> {
  // 1. Insert multiple diverse access logs
  const logA: ICoreAccessLog = await api.functional.core.accessLogs.post(connection, {
    body: {
      user_id: typia.random<string>(),
      session_id: typia.random<string>(),
      event_type: "login",
      ip: "192.168.1.1",
      agent: "Mozilla/5.0 A",
      success: true,
      occurred_at: new Date(Date.now() - 10000).toISOString(),
    } satisfies ICoreAccessLog.ICreate,
  });
  typia.assert(logA);
  const logB: ICoreAccessLog = await api.functional.core.accessLogs.post(connection, {
    body: {
      user_id: typia.random<string>(),
      session_id: null,
      event_type: "api_call",
      ip: "172.16.2.3",
      agent: "Bot/1.0 B",
      success: false,
      occurred_at: new Date(Date.now() - 5000).toISOString(),
    } satisfies ICoreAccessLog.ICreate,
  });
  typia.assert(logB);
  // Optionally add a third log with overlap or more diversity for richer query
  const logC: ICoreAccessLog = await api.functional.core.accessLogs.post(connection, {
    body: {
      user_id: logA.user_id,
      session_id: typia.random<string>(),
      event_type: "logout",
      ip: "192.168.1.1",
      agent: "Mozilla/5.0 A",
      success: true,
      occurred_at: new Date(Date.now()).toISOString(),
    } satisfies ICoreAccessLog.ICreate,
  });
  typia.assert(logC);

  // 2. List logs with no filter (should return all inserted; check data array contains all logs and pagination)
  const allLogs: IPageICoreAccessLog = await api.functional.core.accessLogs.patch(connection, { body: {} });
  typia.assert(allLogs);
  const logIds = [logA.id, logB.id, logC.id];
  const allReturnedIds = allLogs.data.map(x => x.id);
  logIds.forEach(id => TestValidator.true("all logs present")(allReturnedIds.includes(id)));
  TestValidator.equals("pagination type")("object")(typeof allLogs.pagination);

  // 3. Filter: by user_id
  const userIdLogs = await api.functional.core.accessLogs.patch(connection, {
    body: { user_id: logA.user_id },
  });
  typia.assert(userIdLogs);
  userIdLogs.data.forEach(log => TestValidator.equals("user_id")(logA.user_id)(log.user_id));

  //    by event_type
  const eventTypeLogs = await api.functional.core.accessLogs.patch(connection, {
    body: { event_type: "login" },
  });
  typia.assert(eventTypeLogs);
  eventTypeLogs.data.forEach(log => TestValidator.equals("event_type")("login")(log.event_type));

  //    by compound user_id & event_type
  const comboLogs = await api.functional.core.accessLogs.patch(connection, {
    body: { user_id: logA.user_id, event_type: "logout" },
  });
  typia.assert(comboLogs);
  comboLogs.data.forEach(log => {
    TestValidator.equals("user_id")(logA.user_id)(log.user_id);
    TestValidator.equals("event_type")("logout")(log.event_type);
  });

  //    by ip
  const ipLogs = await api.functional.core.accessLogs.patch(connection, {
    body: { ip: "192.168.1.1" },
  });
  typia.assert(ipLogs);
  ipLogs.data.forEach(log => TestValidator.equals("ip")("192.168.1.1")(log.ip));

  // 4. Pagination: limit and page
  const page1 = await api.functional.core.accessLogs.patch(connection, {
    body: { limit: 2, page: 1 },
  });
  typia.assert(page1);
  TestValidator.equals("pagination limit")(2)(page1.pagination.limit);
  TestValidator.equals("pagination current")(1)(page1.pagination.current);
  TestValidator.true("page 1 data length")(page1.data.length <= 2);
  if (page1.pagination.pages > 1) {
    const page2 = await api.functional.core.accessLogs.patch(connection, {
      body: { limit: 2, page: 2 },
    });
    typia.assert(page2);
    TestValidator.equals("pagination limit")(2)(page2.pagination.limit);
    TestValidator.equals("pagination current")(2)(page2.pagination.current);
    TestValidator.true("page 2 data length")(page2.data.length <= 2);
    // Optionally confirm no duplicate id with page1
    page2.data.forEach(log => TestValidator.false("duplicate id on pages")(page1.data.some(l => l.id === log.id)));
  }

  // 5. Time range filter
  const from = logA.occurred_at;
  const to = logC.occurred_at;
  const rangedLogs = await api.functional.core.accessLogs.patch(connection, {
    body: { occurred_at_from: from, occurred_at_to: to },
  });
  typia.assert(rangedLogs);
  rangedLogs.data.forEach(log => {
    TestValidator.true("in range")(log.occurred_at >= from && log.occurred_at <= to);
  });

  // 6. Deterministic output (sorted by occurred_at ascending if possible)
  if (rangedLogs.data.length >= 2) {
    for (let i = 1; i < rangedLogs.data.length; ++i) {
      TestValidator.true("sorted by occurred_at")(
        rangedLogs.data[i].occurred_at >= rangedLogs.data[i - 1].occurred_at,
      );
    }
  }

  // 7. Edge: non-existing user_id -> expect empty data[]
  const emptyLogs = await api.functional.core.accessLogs.patch(connection, {
    body: { user_id: typia.random<string>() },
  });
  typia.assert(emptyLogs);
  TestValidator.equals("empty result array")(0)(emptyLogs.data.length);

  // 8. Negative: malformed filter (bad types)
  await TestValidator.error("malformed filter user_id")(() =>
    api.functional.core.accessLogs.patch(connection, {
      // @ts-expect-error: purposely passing wrong type
      body: { user_id: 12345 },
    }),
  );
}

