import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IPageIProductCatalogProduct } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIProductCatalogProduct";
import { IProductCatalogProduct } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductCatalogProduct";

/**
 * Retrieve list of products using pagination and filtering (by category,
 * price range, availability, etc.). Confirm correct results and test edge
 * cases such as no matches, sorting by name, and invalid filter inputs.
 *
 * 1. Create several sample product records with different names, categories,
 *    statuses, codes, and creation times using the product creation endpoint.
 *    Make sure categories and statuses overlap for later filtering.
 * 2. Query products with no filters  confirm all products appear and
 *    pagination metadata is correct.
 * 3. Filter products by category (if supported in the schema), confirm only
 *    filtered products returned.
 * 4. Filter by status (e.g., active or draft). Check that only matching status
 *    products are present.
 * 5. Search by product name substring (partial/fuzzy match): confirm
 *    correctness.
 * 6. Use pagination: request with small limit and specific page, confirm that
 *    the correct records are returned for that page.
 * 7. Sort by name ASC/DESC (if API supports), and confirm sorting.
 * 8. Run an invalid filter (nonsense status or malformed page parameter) and
 *    confirm error or empty result as defined by business rules.
 * 9. Use a filter that produces no results (e.g., name: "zzzzzz"). Expect
 *    empty data array and valid pagination.
 */
export async function test_api_product_catalog_list_and_filter_products_with_various_criteria(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create sample products
  const categories = ["electronics", "apparel", "grocery"];
  const statuses = ["active", "draft"];
  const createdProducts: IProductCatalogProduct[] = [];
  for (let i = 0; i < 6; ++i) {
    const prod = await api.functional.productCatalog.products.post(connection, {
      body: {
        name: `Sample Product ${i} ${categories[i % categories.length]}`,
        description: `Test product for E2E - ${i}`,
        internal_code: `CODE${i}`,
        status: statuses[i % 2],
        created_by_user_id: crypto.randomUUID(),
        updated_by_user_id: crypto.randomUUID(),
      } satisfies IProductCatalogProduct.ICreate,
    });
    typia.assert(prod);
    createdProducts.push(prod);
  }

  // 2. Retrieve all products (no filter)
  let response = await api.functional.productCatalog.products.patch(connection, {
    body: {},
  });
  typia.assert(response);
  // At least our created products must appear
  TestValidator.assert("all created products found in product list")( () => {
    const ids = new Set(response.data.map((p) => p.id));
    for (const prod of createdProducts) TestValidator.truthy(`product ${prod.id}`)(ids.has(prod.id));
  });

  // 3. Filter by category (if supported)
  if ("category" in (createdProducts[0] as any)) {
    const cat = categories[1];
    response = await api.functional.productCatalog.products.patch(connection, {
      body: { category: cat },
    });
    typia.assert(response);
    for (const product of response.data)
      TestValidator.equals("filter by category")(cat)((product as any).category);
  }

  // 4. Filter by status
  for (const status of statuses) {
    response = await api.functional.productCatalog.products.patch(connection, {
      body: { status },
    });
    typia.assert(response);
    for (const product of response.data)
      TestValidator.equals("status filter")(status)(product.status);
  }

  // 5. Search by product name substring
  {
    const searchFragment = "Sample Product 1";
    response = await api.functional.productCatalog.products.patch(connection, {
      body: { name: searchFragment },
    });
    typia.assert(response);
    for (const product of response.data)
      TestValidator.truthy("name fragment")(product.name.includes(searchFragment));
  }

  // 6. Pagination test: small limit & selected page
  {
    response = await api.functional.productCatalog.products.patch(connection, {
      body: { limit: 2, page: 2 },
    });
    typia.assert(response);
    TestValidator.equals("pagination limit")(2)(response.data.length);
    TestValidator.equals("current page")(2)(response.pagination.current);
  }

  // 7. Sorting by name (manually for test, if API supports sorting param)
  // There is no sort key in IRequest, so this test is not applicable now

  // 8. Invalid filter (nonsense status)
  {
    response = await api.functional.productCatalog.products.patch(connection, {
      body: { status: "nonsense-status" },
    });
    typia.assert(response);
    TestValidator.equals("no records for nonsense status")(0)(response.data.length);
  }
  // 8b. Malformed page parameter
  await TestValidator.httpError("malformed page param")(422)(() =>
    api.functional.productCatalog.products.patch(connection, {
      body: { page: 0 }, // page is Minimum<1>
    })
  );

  // 9. No results
  {
    response = await api.functional.productCatalog.products.patch(connection, {
      body: { name: "zzzzzz" },
    });
    typia.assert(response);
    TestValidator.equals("empty list for no results")(0)(response.data.length);
  }
}

