import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IProductCatalogProduct } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductCatalogProduct";

/**
 * E2E Test: Product creation - success, missing/invalid fields, and uniqueness.
 *
 * This test verifies product creation with all required and optional fields,
 * tests handling of requests with missing/invalid fields, and enforces uniqueness
 * constraints (e.g., internal_code) in the product catalog.
 *
 * Steps:
 * 1. Create a product with all valid required fields (success case)
 * 2. Attempt to create a product with missing required field (should fail)
 * 3. Attempt to create a product with an invalid UUID in created_by_user_id (should fail)
 * 4. Attempt to create another product using the same internal_code (should fail)
 */
export async function test_api_product_create_with_success_and_failure_cases(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a product with all valid required fields
  const productInput: IProductCatalogProduct.ICreate = {
    created_by_user_id: typia.random<string & typia.tags.Format<"uuid">>(),
    updated_by_user_id: typia.random<string & typia.tags.Format<"uuid">>(),
    name: "Apple iPhone 23",
    description: "Latest flagship iPhone for 2025",
    internal_code: `iphone23-${Date.now()}`,
    status: "active",
  };
  const product = await api.functional.productCatalog.products.post(connection, {
    body: productInput,
  });
  typia.assert(product);
  TestValidator.equals("name")(productInput.name)(product.name);
  TestValidator.equals("internal_code")(productInput.internal_code)(product.internal_code);

  // 2. Attempt to create a product with missing required field (should fail)
  const invalidInputMissing: any = { ...productInput };
  delete invalidInputMissing.name;
  await TestValidator.error("missing required field 'name'")(() =>
    api.functional.productCatalog.products.post(connection, {
      body: invalidInputMissing as IProductCatalogProduct.ICreate,
    }),
  );

  // 3. Attempt to create a product with an invalid UUID for created_by_user_id (should fail)
  const invalidInputUUID: IProductCatalogProduct.ICreate = {
    ...productInput,
    created_by_user_id: "invalid-uuid" as any,
    internal_code: productInput.internal_code + "-uuidfail",
  };
  await TestValidator.error("invalid uuid format")(() =>
    api.functional.productCatalog.products.post(connection, {
      body: invalidInputUUID,
    }),
  );

  // 4. Attempt to create another product using the same internal_code (should fail - uniqueness)
  const duplicateInput: IProductCatalogProduct.ICreate = {
    ...productInput,
    name: "iPhone 23 Duplicate",
    internal_code: productInput.internal_code,
  };
  await TestValidator.error("duplicate internal_code")(() =>
    api.functional.productCatalog.products.post(connection, {
      body: duplicateInput,
    }),
  );
}

