  TestValidator.equals("updated name")(updateInput.name)(updated.name);
  TestValidator.equals("updated description")(updateInput.description)(updated.description);
  TestValidator.equals("updated internal_code")(updateInput.internal_code)(updated.internal_code);
  TestValidator.equals("updated status")(updateInput.status)(updated.status);

  // 3. Attempt to update with missing required update fields (missing updated_by_user_id)
