  const variantB: IProductCatalogProductVariant = await api.functional.productCatalog.productVariants.post(connection, {
    body: {
      product_id: productIdB,
      import_feed_id: undefined,
      sku: `SKU-B-${Date.now()}`,
      price: 2000,
      barcode: `BC-B-${Date.now()}`,
      available: false,
    } satisfies IProductCatalogProductVariant.ICreate,
  });
  typia.assert(variantB);

  // 2. List all variants (no filters)
  const allList: IPageIProductCatalogProductVariant = await api.functional.productCatalog.productVariants.patch(connection, {
    body: {},
  });
  typia.assert(allList);
  TestValidator.equals("At least 2 test variants in result")(true)(
    allList.data.some((v) => v.id === variantA.id) &&
    allList.data.some((v) => v.id === variantB.id),
  );

  // 3. Filter by product_id (A only)
  const listA = await api.functional.productCatalog.productVariants.patch(connection, {
    body: { product_id: productIdA },
  });
  typia.assert(listA);
  TestValidator.equals("Only variants of productIdA")(
    true,
  )(
    listA.data.every((v) => v.product_id === productIdA)
  );
  TestValidator.equals("variantA present in productIdA")(
    true,
  )(
    listA.data.some((v) => v.id === variantA.id)
  );

  // 4. Filter by SKU/barcode
  const skuFilter = await api.functional.productCatalog.productVariants.patch(connection, {
    body: { sku: variantB.sku },
  });
  typia.assert(skuFilter);
  TestValidator.equals("Find by SKU")(variantB.id)(skuFilter.data[0]?.id);

  const barcodeFilter = await api.functional.productCatalog.productVariants.patch(connection, {
    body: { barcode: variantA.barcode },
  });
  typia.assert(barcodeFilter);
  TestValidator.equals("Find by barcode")(variantA.id)(barcodeFilter.data[0]?.id);

  // 5. Filter by available=false
  const notAvailableList = await api.functional.productCatalog.productVariants.patch(connection, {
    body: { available: false },
  });
  typia.assert(notAvailableList);
  TestValidator.equals("variantB is only not-available test variant")(true)(
    notAvailableList.data.some((v) => v.id === variantB.id)
  );

  // 6. Pagination (limit=1, offset=1)
  const pageRes1 = await api.functional.productCatalog.productVariants.patch(connection, {
    body: { limit: 1, offset: 1 },
  });
  typia.assert(pageRes1);
  TestValidator.equals("limit is 1")(
    1,
  )(pageRes1.data.length);

  // 7. Edge: filter for non-existent product_id
  const nonExistList = await api.functional.productCatalog.productVariants.patch(connection, {
    body: { product_id: typia.random<string & typia.tags.Format<"uuid">>() },
  });
  typia.assert(nonExistList);
  TestValidator.equals("No results for non-existent product")(
    0,
  )(nonExistList.data.length);

  // 8. Edge: invalid offset
  await TestValidator.httpError("Negative offset is an error")(
    400,
  )(() =>
    api.functional.productCatalog.productVariants.patch(connection, {
      body: { offset: -1 },
    })
  );
}

