/**
 * E2E Test: List and search product categories with filtering, pagination, and validation of edge cases.
 *
 * This function validates correct operation of the product category list/search API, including filtering by name/parent, pagination, empty/no-match results, and error/validation semantics for invalid parameters.
 *
 * Steps:
 * 1. Create a variety of categories including root and subcategories with unique names.
 * 2. List all categories (no filter) and confirm all exist.
 * 3. Search with partial name; confirm matching category is returned and others are not.
 * 4. Filter by parent_id; confirm only children of the specified parent are returned.
 * 5. Pagination: verify slices for limit/offset and non-overlapping results.
 * 6. Invalid input tests: negative limit/offset, non-UUID parent_id; ensure error responses.
 * 7. Name filter with no match: confirm empty result set.
 */
export async function test_api_product_category_list_and_search(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create categories
  const electronics: IProductCategory = await api.functional.productCatalog.productCategories.post(connection, {
    body: {
      name: "Electronics",
    } satisfies IProductCategory.ICreate
  });
  typia.assert(electronics);

  const clothing: IProductCategory = await api.functional.productCatalog.productCategories.post(connection, {
    body: {
      name: "Clothing",
    } satisfies IProductCategory.ICreate
  });
  typia.assert(clothing);

  const mobilePhones: IProductCategory = await api.functional.productCatalog.productCategories.post(connection, {
    body: {
      name: "Mobile Phones",
      parent_id: electronics.id
    } satisfies IProductCategory.ICreate
  });
  typia.assert(mobilePhones);

  const laptops: IProductCategory = await api.functional.productCatalog.productCategories.post(connection, {
    body: {
      name: "Laptops",
      parent_id: electronics.id
    } satisfies IProductCategory.ICreate
  });
  typia.assert(laptops);

  const mensShirts: IProductCategory = await api.functional.productCatalog.productCategories.post(connection, {
    body: {
      name: "Men's Shirts",
      parent_id: clothing.id
    } satisfies IProductCategory.ICreate
  });
  typia.assert(mensShirts);

  const womensDresses: IProductCategory = await api.functional.productCatalog.productCategories.post(connection, {
    body: {
      name: "Women's Dresses",
      parent_id: clothing.id
    } satisfies IProductCategory.ICreate
  });
  typia.assert(womensDresses);

  // 2. List all categories (no filter)
  const allResult: IPageIProductCategory = await api.functional.productCatalog.productCategories.patch(connection, {
    body: {},
  });
  typia.assert(allResult);
  const allNames = allResult.data.map((c) => c.name);
  TestValidator.equals("all categories present")(
    [
      "Electronics",
      "Clothing",
      "Mobile Phones",
      "Laptops",
      "Men's Shirts",
      "Women's Dresses"
    ].sort()
  )(allNames.sort());

  // 3. Search by partial name ('Phone')
  const phonesResult: IPageIProductCategory = await api.functional.productCatalog.productCategories.patch(connection, {
    body: { name: "Phone" },
  });
  typia.assert(phonesResult);
  TestValidator.equals("partial name filter")([
    "Mobile Phones"
  ])(phonesResult.data.map((c) => c.name));

  // 4. Filter by parent_id (Electronics)
  const childrenOfElectronics: IPageIProductCategory = await api.functional.productCatalog.productCategories.patch(connection, {
    body: { parent_id: electronics.id },
  });
  typia.assert(childrenOfElectronics);
  TestValidator.equals("children of Electronics")([
    "Mobile Phones",
    "Laptops"
  ].sort())(childrenOfElectronics.data.map((c) => c.name).sort());

  // 5. Pagination: limit=2, offset=0; then offset=2
  const page0: IPageIProductCategory = await api.functional.productCatalog.productCategories.patch(connection, {
    body: { limit: 2, offset: 0 },
  });
  typia.assert(page0);
  const page1: IPageIProductCategory = await api.functional.productCatalog.productCategories.patch(connection, {
    body: { limit: 2, offset: 2 },
  });
  typia.assert(page1);
  // Ensure no overlap and all entries are covered when slices combined
  const pagedNames = [...page0.data, ...page1.data].map((c) => c.name);
  TestValidator.equals("paging covers unique entries")(pagedNames.length === new Set(pagedNames).size)(true);

  // 6. Invalid input tests
  await TestValidator.error("negative limit")(() =>
    api.functional.productCatalog.productCategories.patch(connection, {
      body: { limit: -5 },
    }),
  );
  await TestValidator.error("negative offset")(() =>
    api.functional.productCatalog.productCategories.patch(connection, {
      body: { limit: 1, offset: -2 },
    }),
  );
  await TestValidator.error("invalid parent_id format")(() =>
    api.functional.productCatalog.productCategories.patch(connection, {
      body: { parent_id: "not-a-uuid" },
    }),
  );

  // 7. Name filter with no matching category
  const noneResult: IPageIProductCategory = await api.functional.productCatalog.productCategories.patch(connection, {
    body: { name: "NonexistentCategoryName" },
  });
  typia.assert(noneResult);
  TestValidator.equals("empty result for unmatched name")(noneResult.data.length)(0);
}

