import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IProductCategory } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductCategory";

/**
 * Validate updating product categories, including hierarchy, atomic updates, and error handling.
 *
 * This test covers multiple scenarios for product category updates:
 * - Normal update of a category's name
 * - Changing category parent_id for hierarchy
 * - Attempt to create a circular hierarchy (parent_id == self or descendant)
 * - Attempt update with invalid data
 * - Attempt update on a non-existent category and check error response
 *
 * Steps:
 * 1. Create root category (A)
 * 2. Create subcategory (B, parent: A)
 * 3. Normal: Update B's name
 * 4. Hierarchy: Move B to root (parent_id: null)
 * 5. Invalid: Attempt to set B's parent_id = B itself (circular)
 * 6. Circular: Attempt to set A's parent_id = B (would create loop)
 * 7. Invalid: Try update with empty name (violates required/unique constraints)
 * 8. Update a non-existent category and expect 404
 */
export async function test_api_product_category_update_and_errors(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create root category (A)
  const root: IProductCategory = await api.functional.productCatalog.productCategories.post(connection, {
    body: {
      name: "RootCategory",
    } satisfies IProductCategory.ICreate,
  });
  typia.assert(root);

  // 2. Create subcategory (B, parent: A)
  const child: IProductCategory = await api.functional.productCatalog.productCategories.post(connection, {
    body: {
      parent_id: root.id,
      name: "ChildCategory",
    } satisfies IProductCategory.ICreate,
  });
  typia.assert(child);

  // 3. Normal: Update B's name
  {
    const updated: IProductCategory = await api.functional.productCatalog.productCategories.putById(connection, {
      id: child.id,
      body: {
        name: "ChildCategoryRenamed",
      } satisfies IProductCategory.IUpdate,
    });
    typia.assert(updated);
    TestValidator.equals("updated B's name")("ChildCategoryRenamed")(updated.name);
  }

  // 4. Hierarchy: Move B to root (parent_id: null)
  {
    const updated: IProductCategory = await api.functional.productCatalog.productCategories.putById(connection, {
      id: child.id,
      body: {
        parent_id: null,
      } satisfies IProductCategory.IUpdate,
    });
    typia.assert(updated);
    TestValidator.equals("moved B to root")(null)(updated.parent_id);
  }

  // 5. Invalid: Attempt to set B's parent_id = B itself (circular)
  await TestValidator.httpError("category cannot have itself as parent")(400)(() =>
    api.functional.productCatalog.productCategories.putById(connection, {
      id: child.id,
      body: { parent_id: child.id } satisfies IProductCategory.IUpdate,
    })
  );

  // 6. Circular: Attempt to set root's parent_id = B (would create loop)
  await TestValidator.httpError("circular category reference")(400)(() =>
    api.functional.productCatalog.productCategories.putById(connection, {
      id: root.id,
      body: { parent_id: child.id } satisfies IProductCategory.IUpdate,
    })
  );

  // 7. Invalid: Try update with empty name (violates required/unique constraints)
  await TestValidator.httpError("empty name invalid")(400)(() =>
    api.functional.productCatalog.productCategories.putById(connection, {
      id: child.id,
      body: { name: "" } satisfies IProductCategory.IUpdate,
    })
  );

  // 8. Update a non-existent category and expect 404
  await TestValidator.httpError("update non-existent category")(404)(() =>
    api.functional.productCatalog.productCategories.putById(connection, {
      id: "dc0e35e3-d755-4eee-9728-9b704b46ee3f", // unlikely UUID
      body: { name: "GhostCategory" } satisfies IProductCategory.IUpdate,
    })
  );
}

