 * 3. Filter by product_id (returns only options for the given product)
 * 4. Filter by option_type (returns only options of that type)
 * 5. Filter by value (returns only matching value)
 * 6. Use invalid product_id, type, or value for filters  assert empty results or appropriate error
 * 7. Validate each PATCH call returns correct IPageIProductOption pagination/data structure
 */
export async function test_api_productOption_list_and_filter_product_options_catalog_management(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create input data (three products/options, two types, several values)
  const productA = typia.random<string & typia.tags.Format<"uuid">>();
  const productB = typia.random<string & typia.tags.Format<"uuid">>();
  const created: IProductOption[] = [];

  const optionDefs: Array<Omit<IProductOption.ICreate, "product_id"> & {product_id: string}> = [
    { product_id: productA, option_type: "color", value: "Red" },
    { product_id: productA, option_type: "size",  value: "L" },
    { product_id: productA, option_type: "size",  value: "M" },
    { product_id: productB, option_type: "color", value: "Blue" },
    { product_id: productB, option_type: "size",  value: "L" },
  ];
  for (const def of optionDefs) {
    const opt: IProductOption = await api.functional.productCatalog.productOptions.post(connection, {
      body: {
        product_id: def.product_id,
        option_type: def.option_type,
        value: def.value,
      } satisfies IProductOption.ICreate,
    });
    typia.assert(opt);
    created.push(opt);
  }

  // 2. List all options (no filter)
  const allOptions: IPageIProductOption = await api.functional.productCatalog.productOptions.patch(connection, { body: {} });
  typia.assert(allOptions);
  // Should include all just-created
  for (const opt of created) {
    TestValidator.equals("option exists in all-options page")(true)(
      allOptions.data.some((o) => o.id === opt.id),
    );
  }

  // 3. Filter by product_id
  const forProductA: IPageIProductOption = await api.functional.productCatalog.productOptions.patch(connection, { body: { product_id: productA } });
  typia.assert(forProductA);
  for (const opt of created.filter((x) => x.product_id === productA)) {
    TestValidator.equals"optionA exists in productA-filtered result"(true)(
      forProductA.data.some((o) => o.id === opt.id),
    );
  }
  TestValidator.equals("only options for productA")(
    forProductA.data.every((o) => o.product_id === productA),
  )(true);

  // 4. Filter by option_type ("color")
  const colors: IPageIProductOption = await api.functional.productCatalog.productOptions.patch(connection, { body: { option_type: "color" } });
  typia.assert(colors);
  for (const opt of created.filter((x) => x.option_type === "color")) {
    TestValidator.equals("color option exists in color-filtered result")(true)(
      colors.data.some((o) => o.id === opt.id),
    );
  }
  TestValidator.equals("only color types")(
    colors.data.every((o) => o.option_type === "color"),
  )(true);

  // 5. Filter by value ("L")
  const sizeL: IPageIProductOption = await api.functional.productCatalog.productOptions.patch(connection, { body: { value: "L" } });
  typia.assert(sizeL);
  for (const opt of created.filter((x) => x.value === "L")) {
    TestValidator.equals("size L exists in value-filtered result")(true)(
      sizeL.data.some((o) => o.id === opt.id),
    );
  }
  TestValidator.equals("only value L")(
    sizeL.data.every((o) => o.value === "L"),
  )(true);

  // 6. Invalid/edge: filter by nonsense product_id returns nothing
  const nonsenseId = typia.random<string & typia.tags.Format<"uuid">>();
  const invalidByProduct: IPageIProductOption = await api.functional.productCatalog.productOptions.patch(connection, { body: { product_id: nonsenseId } });
  typia.assert(invalidByProduct);
  TestValidator.equals("invalid product_id yields empty result")(0)(invalidByProduct.data.length);

  // 6b. Invalid type
  const invalidType: IPageIProductOption = await api.functional.productCatalog.productOptions.patch(connection, { body: { option_type: "nonsenseType" } });
  typia.assert(invalidType);
  TestValidator.equals("invalid type yields empty result")(0)(invalidType.data.length);

  // 6c. Invalid value
  const invalidValue: IPageIProductOption = await api.functional.productCatalog.productOptions.patch(connection, { body: { value: "___nonsenseValue____" } });
  typia.assert(invalidValue);
  TestValidator.equals("invalid value yields empty result")(0)(invalidValue.data.length);

  // 7. All PATCH responses validated for IPageIProductOption shape with typia.assert above.
}

import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";

/**
 * Test updating an existing API client with valid changes (such as rotating a secret) and verify the metadata is updated.
 * Also, attempt to update with invalid or duplicate client information and confirm appropriate error handling and validation.
 *
 * Steps:
 * 1. Create a new API client (valid client_name, contact_email, etc.).
 * 2. Valid update (description, public_key, and/or contact_email to different valid value).
 * 3. Verify response updates only intended fields and preserves immutable fields.
 * 4. Try update with invalid email, empty body, etc. (expect error).
 * 5. Create a second client, then attempt to update first client with duplicate contact_email (should error).
 * 6. Ensure proper error handling for invalid/duplicate fields and that correct values persist after failure.
 */
export async function test_api_client_update_metadata_and_invalid_cases(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create new API client (valid).
  const initial: IApiClients = await api.functional.apiOmnichannel.apiClients.post(connection, {
    body: {
      client_name: `wtest-client-${Date.now()}`,
      contact_email: `test-client1-${Date.now()}@wrtn.io`,
      public_key: "-----BEGIN PUBLIC KEY-----\nMIIBIjAN...IDAQAB\n-----END PUBLIC KEY-----",
      description: "Test client for update scenario.",
    } satisfies IApiClients.ICreate
  });
  typia.assert(initial);

  // 2. Valid update of the client (change description, rotate key, change contact_email).
  const newDescription = `Updated description at ${Date.now()}`;
  const newPublicKey = "-----BEGIN PUBLIC KEY-----\nMIIBNEWKEY...IDAQAB\n-----END PUBLIC KEY-----";
  const newContactEmail = `test-client1b-${Date.now()}@wrtn.io`;
  const updated: IApiClients = await api.functional.apiOmnichannel.apiClients.putById(connection, {
    id: initial.id,
    body: {
      description: newDescription,
      public_key: newPublicKey,
      contact_email: newContactEmail,
    } satisfies IApiClients.IUpdate
  });
  typia.assert(updated);
  TestValidator.equals("updated id")(initial.id)(updated.id);
  TestValidator.equals("updated client_name")(initial.client_name)(updated.client_name);
  TestValidator.equals("updated description")(newDescription)(updated.description);
  TestValidator.equals("updated contact_email")(newContactEmail)(updated.contact_email);
  TestValidator.equals("updated public_key")(newPublicKey)(updated.public_key);
  TestValidator.equals("created_at stable")(initial.created_at)(updated.created_at);

  // 3. Invalid update: invalid email format
  await TestValidator.httpError("invalid email format")(400)(() =>
    api.functional.apiOmnichannel.apiClients.putById(connection, {
      id: initial.id,
      body: { contact_email: "not-an-email" } satisfies IApiClients.IUpdate,
    })
  );

  // 4. Invalid update: empty update (should error, as at least one field is expected to validate update intent)
  await TestValidator.httpError("empty update body")(400)(() =>
    api.functional.apiOmnichannel.apiClients.putById(connection, {
      id: initial.id,
      body: {} satisfies IApiClients.IUpdate,
    })
  );

  // 5. Create another client for duplication test
  const other: IApiClients = await api.functional.apiOmnichannel.apiClients.post(connection, {
    body: {
      client_name: `wtest-client2-${Date.now()}`,
      contact_email: `test-client2-${Date.now()}@wrtn.io`,
      public_key: "-----BEGIN PUBLIC KEY-----\nMIIBOtherKEY...IDAQAB\n-----END PUBLIC KEY-----",
      description: "Second test client.",
    } satisfies IApiClients.ICreate,
  });
  typia.assert(other);

  // 6. Attempt updating first client with duplicate contact_email (should error if unique enforced)
  await TestValidator.httpError("duplicate contact_email")(409)(() =>
    api.functional.apiOmnichannel.apiClients.putById(connection, {
      id: initial.id,
      body: { contact_email: other.contact_email } satisfies IApiClients.IUpdate,
    })
  );

  // 7. After failure, ensure original client's email and other data are intact
  const afterDuplication: IApiClients = updated.id
    ? await api.functional.apiOmnichannel.apiClients.putById(connection, {
        id: updated.id,
        body: {}, // GET would be more correct, but using PUT to ensure it's not changed by failed op
      })
    : updated;
  typia.assert(afterDuplication);
  TestValidator.equals("contact_email unaffected")(newContactEmail)(afterDuplication.contact_email);
}

import { TestValidator } from "@nestia/e2e";
