/**
 * Test logically deleting an API client.
 *
 * 1. Create a new API client entry.
 * 2. Logically delete (soft-delete) the API client by ID.
 * 3. Validate the returned object has is_active=false, deleted_at set.
 * 4. (If query endpoints existed) Confirm the client is not returned in active queries but remains in audit records.
 * 5. Attempt to delete the same client again, expect error response.
 * 6. Attempt to delete a non-existent client, expect error response.
 */
export async function test_api_client_soft_delete_and_error_on_missing(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a new API client
  const created: IApiClients = await api.functional.apiOmnichannel.apiClients.post(connection, {
    body: {
      client_name: `test-client-${Date.now()}`,
      contact_email: `tester-${Date.now()}@e2e.test`,
      public_key: "test-public-key",
      description: "Soft-delete test client",
    } satisfies IApiClients.ICreate,
  });
  typia.assert(created);

  // 2. Logically delete the client
