import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import { IApiToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiToken";
import { IApiTokens } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiTokens";
import { IPageIApiTokens } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIApiTokens";

/**
 * E2E test to search API tokens with and without filter criteria.
 *
 * This test verifies:
 * - Creation of an API client.
 * - Issuance of at least one API token for that client.
 * - That searching for tokens with no filters returns only active (non-revoked, unexpired) tokens and correct pagination.
 * - That filtering by api_client_id returns only that client's tokens.
 * - That filtering with 'active: false' returns revoked/expired tokens if any exist.
 * - That after revocation, token search reflects correct active/inactive state.
 *
 * Steps:
 * 1. Create API client.
 * 2. Issue API token(s) for client.
 * 3. Search API tokens without filters (should include fresh tokens).
 * 4. Search with api_client_id filter (should see only client's tokens).
 * 5. (Optional) Revoke a token, then search with active=false to confirm it shows up as inactive.
 */
export async function test_api_token_list_search_with_and_without_filters(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create API client
  const clientInput: IApiClients.ICreate = {
    client_name: `E2E-Test-Client-${Date.now()}`,
    contact_email: `e2e-token-test-${Date.now()}@example.com`,
    public_key: undefined,
    description: "E2E test client for API token search",
  };
  const apiClient: IApiClients = await api.functional.apiOmnichannel.apiClients.post(connection, { body: clientInput });
  typia.assert(apiClient);

  // 2. Issue API token for the created client
  const now = new Date();
  const tokenInput: IApiToken.ICreate = {
    api_client_id: apiClient.id,
    scope: "read:api-data",
    issued_at: now.toISOString(),
    expires_at: new Date(now.getTime() + 7 * 24 * 60 * 60 * 1000).toISOString(), // +7 days
  };
  const issuedToken: IApiToken = await api.functional.apiOmnichannel.apiTokens.post(connection, { body: tokenInput });
  typia.assert(issuedToken);

  // 3. Search API tokens with no filters (default listing)
  const tokenSearchAll: IPageIApiTokens = await api.functional.apiOmnichannel.apiTokens.patch(connection, {
    body: {} satisfies IApiTokens.IRequest,
  });
  typia.assert(tokenSearchAll);
  // Ensure the issued token is present and active
  const foundToken = tokenSearchAll.data.find((t) => t.id === issuedToken.id);
  TestValidator.equals("issued token present in all tokens")(true)(!!foundToken);
  TestValidator.equals("token active")(true)(foundToken?.active !== false && !foundToken?.revoked_at);

  // Pagination checks
  TestValidator.equals("pagination type")("object")(typeof tokenSearchAll.pagination === "object" ? "object" : typeof tokenSearchAll.pagination);
  TestValidator.equals("data is array")(true)(Array.isArray(tokenSearchAll.data));

  // 4. Search API tokens by api_client_id
  const tokenSearchByClient: IPageIApiTokens = await api.functional.apiOmnichannel.apiTokens.patch(connection, {
    body: { api_client_id: apiClient.id } satisfies IApiTokens.IRequest,
  });
  typia.assert(tokenSearchByClient);
  // All tokens should match the client id
  for (const token of tokenSearchByClient.data) {
    TestValidator.equals("all tokens for client id")(apiClient.id)(token.api_client_id);
  }
  TestValidator.equals("issued token present in client filtered tokens")(true)(!!tokenSearchByClient.data.find((t) => t.id === issuedToken.id));

  // 5. (Optional) If supported, attempt to search with active: false (should be empty or only show revoked/expired tokens)
  const tokenSearchInactive: IPageIApiTokens = await api.functional.apiOmnichannel.apiTokens.patch(connection, {
    body: { active: false } satisfies IApiTokens.IRequest,
  });
  typia.assert(tokenSearchInactive);
  // The fresh token should not appear
  TestValidator.equals("fresh token absent when filtering for inactive")(false)(!!tokenSearchInactive.data.find((t) => t.id === issuedToken.id));
}

