import { TestValidator } from "@nestia/e2e";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import { IApiToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiToken";

/**
 * Update an API token metadata and verify changes are effective, then test proper error handling for revoked and non-existent tokens.
 *
 * Steps:
 * 1. Create an API client
 * 2. Issue an API token for this client
 * 3. Update the token's scope and expiration. Assert changes applied.
 * 4. Revoke the token by setting revoked_at. Assert revoked state.
 * 5. Attempt update on this token after revocation: expect error.
 * 6. Attempt update on a random (non-existent) token: expect error.
 */
export async function test_api_token_update_and_error_cases(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create an API client
  const client: IApiClients = await api.functional.apiOmnichannel.apiClients.post(connection, {
    body: {
      client_name: `Test Client ${Math.random().toString(36).substring(2, 8)}`,
      contact_email: `test${Date.now()}@example.com`,
      description: "E2E Update scenario client",
      public_key: "test-public-key",
    } satisfies IApiClients.ICreate,
  });
  typia.assert(client);

  // 2. Issue an API token for the client
  const issuedAt = new Date();
  const expiresAt = new Date(Date.now() + 1000 * 60 * 60 * 24 * 10); // 10 days later
  const token: IApiToken = await api.functional.apiOmnichannel.apiTokens.post(connection, {
    body: {
      api_client_id: client.id,
      scope: "read:all write:meta",
      issued_at: issuedAt.toISOString(),
      expires_at: expiresAt.toISOString(),
    } satisfies IApiToken.ICreate,
  });
  typia.assert(token);

  // 3. Update the token (scope + expiration)
  const newScope = "read:all write:meta:extra";
  const newExpiresAt = new Date(Date.now() + 1000 * 60 * 60 * 24 * 60); // 60 days later
  const updated: IApiToken = await api.functional.apiOmnichannel.apiTokens.putById(connection, {
    id: token.id,
    body: {
      scope: newScope,
      expires_at: newExpiresAt.toISOString(),
    } satisfies IApiToken.IUpdate,
  });
  typia.assert(updated);
  TestValidator.equals("updated token id")(token.id)(updated.id);
  TestValidator.equals("updated api_client_id")(token.api_client_id)(updated.api_client_id);
  TestValidator.equals("new scope")(newScope)(updated.scope);
  TestValidator.equals("new expiration")(newExpiresAt.toISOString())(updated.expires_at);
  // Should not have revoked_at at this step
  TestValidator.equals("still not revoked")(undefined)(updated.revoked_at);

  // 4. Revoke the token (apply revoked_at)
  const revokedAt = new Date();
  const revoked: IApiToken = await api.functional.apiOmnichannel.apiTokens.putById(connection, {
    id: token.id,
    body: {
      revoked_at: revokedAt.toISOString(),
    } satisfies IApiToken.IUpdate,
  });
  typia.assert(revoked);
  TestValidator.equals("revoked token id")(token.id)(revoked.id);
  TestValidator.equals("token is revoked")(revokedAt.toISOString())(revoked.revoked_at);

  // 5. Attempt update after revocation (should fail)
  await TestValidator.httpError("update on revoked token should fail")(400)(() =>
    api.functional.apiOmnichannel.apiTokens.putById(connection, {
      id: token.id,
      body: {
        scope: "illegal:update-on-revoked-token",
      } satisfies IApiToken.IUpdate,
    })
  );

  // 6. Attempt update on random (non-existent) token
  await TestValidator.httpError("update on non-existent token should fail")(404)(() =>
    api.functional.apiOmnichannel.apiTokens.putById(connection, {
      id: typia.random<string & tags.Format<"uuid">>(),
      body: {
        scope: "nonexistent:token",
      } satisfies IApiToken.IUpdate,
    })
  );
}

