 * 2. Issue a token to the API client.
 * 3. Revoke the issued token by its id.
 * 4. Attempt to revoke the same token again; expect an error because already revoked.
 * 5. Attempt to revoke a non-existent token id; expect an error.
 */
export async function test_api_token_revoke_and_error_scenarios(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create API client
  const client: IApiClients = await api.functional.apiOmnichannel.apiClients.post(connection, {
    body: {
      client_name: `client-${Math.random().toString(36).slice(2)}`,
      contact_email: `testapi-${Math.random().toString(36).slice(2)}@wrtn.io`,
    } satisfies IApiClients.ICreate,
  });
  typia.assert(client);

  // 2. Issue a token for that client
  const now = new Date();
  const expires = new Date(now.getTime() + 7 * 24 * 60 * 60 * 1000); // +7d
  const token: IApiToken = await api.functional.apiOmnichannel.apiTokens.post(connection, {
    body: {
      api_client_id: client.id,
      scope: "basic:read",
      issued_at: now.toISOString(),
      expires_at: expires.toISOString(),
    } satisfies IApiToken.ICreate,
  });
  typia.assert(token);

  // 3. Revoke (erase) the token
  const revoked: IApiToken = await api.functional.apiOmnichannel.apiTokens.eraseById(connection, {
    id: token.id,
  });
  typia.assert(revoked);
  TestValidator.equals("revoked token id")(token.id)(revoked.id);
  TestValidator.equals("revoked_at is set")(true)(!!revoked.revoked_at);

  // 4. Attempt to revoke the same token again (expect error)
  await TestValidator.httpError("revoke already revoked token")(404)(() =>
    api.functional.apiOmnichannel.apiTokens.eraseById(connection, {
      id: token.id,
    }),
  );

  // 5. Attempt to revoke a non-existent token id (expect error)
  await TestValidator.httpError("revoke non-existent token")(404)(() =>
    api.functional.apiOmnichannel.apiTokens.eraseById(connection, {
      id: typia.random<string & tags.Format<"uuid">>(),
    }),
  );
}

import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IApiAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiAudit";
import { IPageIApiAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIApiAudit";

/**
 * List/search API audit entries with various filter conditions and confirm correct ordering and evidence inclusion in each response.
 *
 * This test covers no-result, single-result, and pagination edge cases.
 *
 * Process:
 * 1. Create multiple audit entries (different endpoints, actors, status, time).
 * 2. Query audit list with no filters; assert all entries returned as expected.
 * 3. Query with single-field filters (endpoint, actor_id, is_success, status_code); results must only match filter.
 * 4. Query with filter that matches no entry (e.g., non-existent endpoint); result must be empty.
 * 5. Query with pagination (limit=2); assert correct slicing and metadata.
 * 6. Query with multiple conditions to get single entry; assert correctness.
 * 7. Assert evidence fields (IDs, hashes) present and formatted on every result.
 * 8. Timestamps/order validations using created_at.
 */
export async function test_list_api_audits_with_various_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create multiple diverse audit entries
  const now = new Date();
  const makeIso = (d: Date) => d.toISOString();
  const auditInputs: IApiAudit.ICreate[] = [
    {
      api_client_id: typia.random<string & typia.tags.Format<"uuid">>(),
      api_token_id: typia.random<string & typia.tags.Format<"uuid">>(),
      endpoint: "/endpoint/A",
      http_method: "GET",
      actor_id: typia.random<string & typia.tags.Format<"uuid">>(),
      is_success: true,
      status_code: 200,
      created_at: makeIso(new Date(now.getTime() - 30000)), // 30s ago
      decision_log_id: "DL1",
      request_hash: "hashA",
      response_hash: "hashRespA",
    },
    {
      api_client_id: typia.random<string & typia.tags.Format<"uuid">>(),
      api_token_id: typia.random<string & typia.tags.Format<"uuid">>(),
      endpoint: "/endpoint/B",
      http_method: "POST",
      actor_id: typia.random<string & typia.tags.Format<"uuid">>(),
      is_success: false,
      status_code: 403,
      created_at: makeIso(new Date(now.getTime() - 20000)), // 20s ago
      decision_log_id: "DL2",
      request_hash: "hashB",
      response_hash: "hashRespB",
    },
    {
      api_client_id: typia.random<string & typia.tags.Format<"uuid">>(),
      api_token_id: typia.random<string & typia.tags.Format<"uuid">>(),
      endpoint: "/endpoint/C",
      http_method: "PUT",
      actor_id: typia.random<string & typia.tags.Format<"uuid">>(),
      is_success: true,
      status_code: 201,
      created_at: makeIso(new Date(now.getTime() - 10000)), // 10s ago
      decision_log_id: "DL3",
      request_hash: "hashC",
      response_hash: "hashRespC",
    },
  ];
  const created: IApiAudit[] = [];
  for (const input of auditInputs) {
    const entry: IApiAudit = await api.functional.apiOmnichannel.apiAudits.post(connection, { body: input });
    typia.assert(entry);
    created.push(entry);
  }

  // 2. Query  all (no filter)
  const allPage: IPageIApiAudit = await api.functional.apiOmnichannel.apiAudits.patch(connection, { body: {} });
  typia.assert(allPage);
  // 2a. Validate required shape and all created audits returned.
  const allAuditIds = created.map(e => e.id).sort();
  const listedIds = allPage.data.map(e => e.id).filter(id => allAuditIds.includes(id)).sort();
  TestValidator.equals("all listed IDs correct")(allAuditIds)(listedIds);
  // 2b. Each has evidence fields, correct format
  for (const entry of allPage.data) {
    typia.assert(entry);
    TestValidator.equals("has api_client_id")(true)(!!entry.api_client_id);
    TestValidator.equals("has api_token_id")(true)(!!entry.api_token_id);
    TestValidator.equals("has actor_id")(true)(!!entry.actor_id);
    TestValidator.equals("has evidence hashes")(true)(!!entry.request_hash && !!entry.response_hash);
  }
  // 2c. Created_at descending order
  let last = "9999-12-31T23:59:59Z";
  for (const entry of allPage.data) {
    TestValidator.equals("created_at order")(true)(entry.created_at <= last);
    last = entry.created_at;
  }

  // 3. Single-field filter: endpoint
  const endpointARes = await api.functional.apiOmnichannel.apiAudits.patch(connection, { body: { endpoint: "/endpoint/A" } });
  TestValidator.equals("endpointA count")(1)(endpointARes.data.length);
  TestValidator.equals("endpointA endpoint matches")(["/endpoint/A"])(endpointARes.data.map(e => e.endpoint));

  // 4. Single-field filter: actor_id, is_success, status_code
  const actorId = created[1].actor_id;
  const actorRes = await api.functional.apiOmnichannel.apiAudits.patch(connection, { body: { actor_id: actorId } });
  TestValidator.equals("actor filter")(true)(actorRes.data.some(e => e.actor_id === actorId));
  const failRes = await api.functional.apiOmnichannel.apiAudits.patch(connection, { body: { is_success: false } });
  TestValidator.equals("failed only")(true)(failRes.data.every(e => !e.is_success));
  const statusRes = await api.functional.apiOmnichannel.apiAudits.patch(connection, { body: { status_code: 201 } });
  TestValidator.equals("status code ==201")(true)(statusRes.data.every(e => e.status_code === 201));

  // 5. Filter  Non-existent endpoint (no results)
  const noneRes = await api.functional.apiOmnichannel.apiAudits.patch(connection, { body: { endpoint: "/no/such/endpoint" } });
  TestValidator.equals("no data")(0)(noneRes.data.length);

  // 6. Pagination  limit=2
  const pagedRes = await api.functional.apiOmnichannel.apiAudits.patch(connection, { body: { limit: 2 } });
  typia.assert(pagedRes);
  TestValidator.equals("paged limit")(2)(pagedRes.data.length);
  TestValidator.equals("paged meta limit")(2)(pagedRes.pagination.limit);
  TestValidator.equals("page number")(1)(pagedRes.pagination.current);
  TestValidator.equals("records count")(allPage.pagination.records)(pagedRes.pagination.records);
  TestValidator.equals("pages count")(Math.ceil(allPage.pagination.records / 2))(pagedRes.pagination.pages);

  // 7. Combined filter: Find unique entry (by actor+endpoint+status)
  const oneCombo = created[2];
  const singleRes = await api.functional.apiOmnichannel.apiAudits.patch(connection, {
    body: {
      endpoint: oneCombo.endpoint,
      actor_id: oneCombo.actor_id,
      status_code: oneCombo.status_code,
      is_success: oneCombo.is_success,
    },
  });
  TestValidator.equals("single entry combo")(1)(singleRes.data.length);
  TestValidator.equals("entry id")(oneCombo.id)(singleRes.data[0].id);

  // 8. (Optional) Time window filter
  const since = new Date(now.getTime() - 25000).toISOString();
  const rangeRes = await api.functional.apiOmnichannel.apiAudits.patch(connection, {
    body: { created_from: since },
  });
  TestValidator.equals("range newer count")(2)(rangeRes.data.length);
  for (const entry of rangeRes.data) {
    TestValidator.equals("created_at >= since")(true)(entry.created_at >= since);
  }
}

import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
