import { TestValidator } from "@nestia/e2e";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IApiAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiAudit";

/**
 * E2E test for updating allowed metadata fields and handling restricted fields in API audit (api_audits) entry.
 *
 * Scenario logic:
 * 1. Create a new API audit entry using POST (apiOmnichannel.apiAudits.post) with valid required data.
 * 2. Update allowed metadata fields via PUT (apiOmnichannel.apiAudits.putById) to verify that permitted changes are accepted.
 * 3. Assert the response contains the updated field values.
 * 4. Attempt to update forbidden/restricted fields (e.g., add id to input object for PUT)should reject or error.
 * 5. Assert proper error/rejection occurs for restricted field update attempt.
 */
export async function test_api_audit_update_metadata_and_handle_restricted_fields(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a new audit entry
  const createInput: IApiAudit.ICreate = {
    api_client_id: typia.random<string & tags.Format<"uuid">>(),
    api_token_id: typia.random<string & tags.Format<"uuid">>(),
    endpoint: "/api/v1/test-resource",
    http_method: "GET",
    actor_id: typia.random<string & tags.Format<"uuid">>(),
    is_success: true,
    status_code: 200,
    created_at: new Date().toISOString(),
    request_hash: "hash_request_123",
    response_hash: "hash_response_456",
  };
  const created: IApiAudit = await api.functional.apiOmnichannel.apiAudits.post(connection, { body: createInput });
  typia.assert(created);

  // 2. Update certain allowed fields (change endpoint and is_success)
  const updateInput: IApiAudit.IUpdate = {
    api_client_id: created.api_client_id,
    api_token_id: created.api_token_id,
    integration_channel_id: created.integration_channel_id ?? null,
    endpoint: "/api/v1/test-resource-updated",
    http_method: "PUT",
    actor_id: created.actor_id,
    decision_log_id: created.decision_log_id ?? null,
    request_hash: "hash_request_789",
    response_hash: "hash_response_999",
    is_success: false,
    status_code: 503,
    created_at: created.created_at,
  };
  const updated: IApiAudit = await api.functional.apiOmnichannel.apiAudits.putById(connection, { id: created.id, body: updateInput });
  typia.assert(updated);
  // 3. Validate updated fields are changed
  TestValidator.equals("endpoint field changed")("/api/v1/test-resource-updated")(updated.endpoint);
  TestValidator.equals("http_method updated")("PUT")(updated.http_method);
  TestValidator.equals("request_hash updated")("hash_request_789")(updated.request_hash);
  TestValidator.equals("response_hash updated")("hash_response_999")(updated.response_hash);
  TestValidator.equals("is_success updated")(false)(updated.is_success);
  TestValidator.equals("status_code updated")(503)(updated.status_code);

  // 4. Attempt to update with restricted field (injecting id into body, which is not permitted)
  // @ts-expect-error (illustration: forcibly add id property)
  const invalidUpdate = { ...updateInput, id: "illegal_id_in_update_body" };
  await TestValidator.error("Updating with restricted id field should fail")(() =>
    api.functional.apiOmnichannel.apiAudits.putById(connection, { id: created.id, body: invalidUpdate })
  );
}

