import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IIntegrationChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/IIntegrationChannel";
import { IPageIIntegrationChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIIntegrationChannel";

/**
 * E2E test for searching integration channels with various filters, including pagination and sorting.
 *
 * 1. Create several integration channel records (diverse channel_code, channel_name, is_active).
 * 2. Search by channel_code and confirm only the matching record is returned.
 * 3. Search by channel_name (partial match); confirm correct set.
 * 4. Search by activation status (is_active); confirm correct results.
 * 5. Combined filter (channel_code + is_active); confirm intersection.
 * 6. Filter for no matches; assert empty result.
 * 7. Paginate results (limit=2, page control); check correct slicing.
 * 8. Sort by channel_name (asc/desc); check orderings.
 * 9. No deletion cleanup (records should persist as per audit requirements).
 */
export async function test_api_integration_channel_search_with_various_filters(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create several integration channel records
  const channelsToCreate: Array<{
    code: string;
    name: string;
    is_active: boolean;
  }> = [
    { code: "web", name: "Website", is_active: true },
    { code: "mobile", name: "MobileApp", is_active: true },
    { code: "partner_erp", name: "ERP Partner", is_active: false },
    { code: "inhouse", name: "Wrtn Inhouse", is_active: true },
    { code: "archive", name: "Old Archive", is_active: false },
  ];
  const createdChannels: IIntegrationChannel[] = [];
  for (const input of channelsToCreate) {
    const record = await api.functional.apiOmnichannel.integrationChannels.post(
      connection,
      {
        body: {
          channel_code: input.code,
          channel_name: input.name,
        } satisfies IIntegrationChannel.ICreate,
      },
    );
    typia.assert(record);
    // Patch for is_active status if default differs (simulate, as .post does not expose is_active in create)
    // For scenario, we rely only on that some are later inactive.
    createdChannels.push({ ...record, is_active: input.is_active });
  }

  // 2. Search with channel_code filter (exact)
  for (const ch of createdChannels) {
    const result = await api.functional.apiOmnichannel.integrationChannels.patch(
      connection,
      {
        body: {
          filters: { channel_code: ch.channel_code },
        } satisfies IIntegrationChannel.IRequest,
      },
    );
    typia.assert(result);
    TestValidator.equals("exact channel_code match count")(1)(result.data.length);
    TestValidator.equals("exact channel_code match id")(ch.channel_code)(result.data[0].channel_code);
  }

  // 3. Search with channel_name partial/fuzzy match
  const substring = "App";
  const expectedNameMatches = createdChannels.filter(ch => ch.channel_name.includes(substring));
  const fuzzyResult = await api.functional.apiOmnichannel.integrationChannels.patch(
    connection,
    {
      body: {
        filters: { channel_name: substring },
      },
    },
  );
  typia.assert(fuzzyResult);
  TestValidator.equals("partial channel_name matches count")(expectedNameMatches.length)(fuzzyResult.data.length);
  for (const c of expectedNameMatches) {
    TestValidator.equals("fuzzy channel_name contains")(true)(
      fuzzyResult.data.some(rcd => rcd.channel_name === c.channel_name),
    );
  }

  // 4. Search with is_active true and false
  for (const flag of [true, false]) {
    const result = await api.functional.apiOmnichannel.integrationChannels.patch(
      connection,
      {
        body: {
          filters: { is_active: flag },
        },
      },
    );
    typia.assert(result);
    const expected = createdChannels.filter(ch => ch.is_active === flag);
    TestValidator.equals(`is_active ${flag} count`)(expected.length)(result.data.length);
    for (const c of expected) {
      TestValidator.equals("is_active match")(flag)(result.data.find(rcd => rcd.channel_code === c.channel_code)?.is_active);
    }
  }

  // 5. Combined filter (channel_code + is_active)
  for (const ch of createdChannels) {
    const result = await api.functional.apiOmnichannel.integrationChannels.patch(
      connection,
      {
        body: {
          filters: { channel_code: ch.channel_code, is_active: ch.is_active },
        },
      },
    );
    typia.assert(result);
    const expected = createdChannels.find(cc => cc.channel_code === ch.channel_code && cc.is_active === ch.is_active);
    if (expected) {
      TestValidator.equals("combined filter count")(1)(result.data.length);
      TestValidator.equals("combined filter channel_code")(ch.channel_code)(result.data[0].channel_code);
      TestValidator.equals("combined filter is_active")(ch.is_active)(result.data[0].is_active);
    } else {
      TestValidator.equals("combined filter none")(0)(result.data.length);
    }
  }

  // 6. Search with a filter that should return no results
  const emptyResult = await api.functional.apiOmnichannel.integrationChannels.patch(
    connection,
    {
      body: {
        filters: { channel_code: "definitely_not_existing_code" },
      },
    },
  );
  typia.assert(emptyResult);
  TestValidator.equals("no match for bogus code")(0)(emptyResult.data.length);

  // 7. Pagination edge cases: limit=2 by page
  const total = createdChannels.length;
  let page = 1;
  let sliced: IIntegrationChannel[] = [];
  // Accumulate all paged data for full comparison
  while (sliced.length < total) {
    const pageResult = await api.functional.apiOmnichannel.integrationChannels.patch(
      connection,
      {
        body: {
          pagination: { limit: 2, page },
          sort: { by: "created_at", order: "asc" },
        },
      },
    );
    typia.assert(pageResult);
    // The returned count may be less than 2 on the last page
    TestValidator.equals(`pagination, page ${page} validity`)(
      Math.min(2, total - sliced.length),
    )(pageResult.data.length);
    sliced = sliced.concat(pageResult.data);
    ++page;
    if (pageResult.data.length === 0) break; // Edge break
  }
  // Ensure all returned items match at the end
  const createdCodes = createdChannels.map(c => c.channel_code).sort();
  const returnedCodes = sliced.map(c => c.channel_code).sort();
  TestValidator.equals("all created channels returned via paging")(createdCodes)(returnedCodes);

  // 8. Check sort by channel_name (asc)
  const ascResult = await api.functional.apiOmnichannel.integrationChannels.patch(
    connection,
    {
      body: {
        sort: { by: "channel_name", order: "asc" },
      },
    },
  );
  typia.assert(ascResult);
  const ascNames = [...ascResult.data.map(c => c.channel_name)];
  for (let i = 1; i < ascNames.length; ++i)
