import typia from "typia";
import { TestValidator } from "@nestia/e2e";

import api from "@ORGANIZATION/PROJECT-api";
import { IChannelSession } from "@ORGANIZATION/PROJECT-api/lib/structures/IChannelSession";
import { IPageIChannelSession } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIChannelSession";

/**
 * Search and paginate channel sessions by different filtering fields.
 *
 * This test creates several channel session records, then exercises search, filtering, pagination,
 * and sort features of the API. Ensures proper pagination metadata, filtering, and no deleted sessions.
 *
 * 1. Creates multiple channel sessions with different integration_channel_id, api_token_id, user_id, session_key, started_at/ended_at.
 * 2. Searches with no filters: checks all created sessions are returned, paginated.
 * 3. Paginates result (limit=2), checks partitioning and returned ids per page.
 * 4. Searches with filter by integration_channel_id and user_id, verifies filtered results.
 * 5. Sorts results ascending/descending by started_at, verifies order.
 * 6. Validates all returned sessions are from created records.
 * 7. Validates pagination meta fields.
 * 8. Checks query with unmatched filters returns empty results.
 * 9. Checks out-of-bounds pagination returns empty data and correct meta.
 */
export async function test_api_channel_session_search_and_paginate_results(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create several channel session records
  const baseTime = new Date();
  const ids: string[] = [];
  const created: IChannelSession[] = [];
  for (let i = 0; i < 5; ++i) {
    const integration_channel_id = typia.random<string & typia.tags.Format<"uuid">>();
    const api_token_id = typia.random<string & typia.tags.Format<"uuid">>();
    const user_id = typia.random<string & typia.tags.Format<"uuid">>();
    const session_key = `sess-key-${Math.random().toString(36).slice(2)}`;
    const started_at = new Date(baseTime.getTime() + i * 60000).toISOString();
    const ended_at = i % 2 === 0 ? undefined : new Date(baseTime.getTime() + i * 60000 + 1234).toISOString();
    const session: IChannelSession = await api.functional.apiOmnichannel.channelSessions.post(connection, {
      body: {
        integration_channel_id,
        api_token_id,
        user_id,
        session_key,
        started_at,
      } satisfies IChannelSession.ICreate,
    });
    typia.assert(session);
    ids.push(session.id);
    created.push(session);
  }

  // 2. Patch: Search sessions with no filters
  let pageResp: IPageIChannelSession = await api.functional.apiOmnichannel.channelSessions.patch(connection, {
    body: {},
  });
  typia.assert(pageResp);
  TestValidator.equals("no filter - count at least created count")(
    true
  )(pageResp.data.length >= created.length);
  // All created sessions must exist in the result page(s)
  const returnedIds = pageResp.data.map(r => r.id);
  for (const id of ids) {
    TestValidator.equals("session exists in no-filter response")(true)(returnedIds.includes(id));
  }

  // 3. Pagination - limit 2
  let page1: IPageIChannelSession = await api.functional.apiOmnichannel.channelSessions.patch(connection, {
    body: { pagination: { page: 1, limit: 2 } },
  });
  let page2: IPageIChannelSession = await api.functional.apiOmnichannel.channelSessions.patch(connection, {
    body: { pagination: { page: 2, limit: 2 } },
  });
  typia.assert(page1); typia.assert(page2);
  TestValidator.equals("pagination - page size")(2)(page1.data.length);
  TestValidator.equals("pagination - next page exists")(
    true
  )(page2.data.length > 0);
  // aggregate all paginated ids and compare to created ids
  const pagedIds = [...page1.data, ...page2.data].map(s => s.id);
  for (const id of pagedIds) typia.assert(typeof id === "string");

  // 4. Filtering by integration_channel_id and user_id
  const pickSession = created[2];
  const filterResp = await api.functional.apiOmnichannel.channelSessions.patch(connection, {
    body: {
      filters: {
        integration_channel_id: pickSession.integration_channel_id,
        user_id: pickSession.user_id,
      },
    },
  });
  typia.assert(filterResp);
  TestValidator.equals("filtered only one session exists")(
    1
  )(filterResp.data.length);
  TestValidator.equals("filtered correct session returned")(
    pickSession.id
  )(filterResp.data[0]?.id);

  // 5. Sorting results
  const ascResp = await api.functional.apiOmnichannel.channelSessions.patch(connection, {
    body: { sort: { by: "started_at", order: "asc" }, pagination: { limit: 5 } },
  });
  typia.assert(ascResp);
  for (let i = 1; i < ascResp.data.length; ++i) {
    TestValidator.equals("ascending sort")(
      true
    )(ascResp.data[i].started_at >= ascResp.data[i - 1].started_at);
  }
  const descResp = await api.functional.apiOmnichannel.channelSessions.patch(connection, {
    body: { sort: { by: "started_at", order: "desc" }, pagination: { limit: 5 } },
  });
  typia.assert(descResp);
  for (let i = 1; i < descResp.data.length; ++i) {
    TestValidator.equals("descending sort")(
      true
    )(descResp.data[i].started_at <= descResp.data[i - 1].started_at);
  }

  // 6. No deleted check (model only creates non-deleted here, so all we see are created IDs)
  const allIdsReturned = ascResp.data.concat(descResp.data).map(x => x.id);
  for (const id of ids) {
    TestValidator.equals("created session always returned")(
      true
    )(allIdsReturned.includes(id));
  }

  // 7. Pagination meta
  TestValidator.equals("pagination meta structure")(
    true,
  )(
    typeof ascResp.pagination.current === "number" &&
      typeof ascResp.pagination.limit === "number" &&
      typeof ascResp.pagination.records === "number" &&
      typeof ascResp.pagination.pages === "number"
  );

  // 8. Edge Case: unmatched filter
  const emptyResp = await api.functional.apiOmnichannel.channelSessions.patch(connection, {
    body: { filters: { user_id: typia.random<string & typia.tags.Format<"uuid">>() } },
  });
  typia.assert(emptyResp);
  TestValidator.equals("no result for unmatched filters")(0)(emptyResp.data.length);

  // 9. Edge Case: out-of-bounds page
  const bigPageResp = await api.functional.apiOmnichannel.channelSessions.patch(connection, {
    body: { pagination: { page: 9999, limit: 2 } },
  });
  typia.assert(bigPageResp);
  TestValidator.equals("empty data when page is too high")(0)(bigPageResp.data.length);
}

