import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IPageIWebhookEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIWebhookEvent";
import { IWebhookEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/IWebhookEvent";

/**
 * Validate the searching, filtering, and pagination functionality of webhook event listings.
 *
 * This test ensures that the /apiOmnichannel/webhookEvents PATCH endpoint returns webhook events matching search criteria
 * such as creation window, destination URL, and delivery status, and that pagination metadata and records are correct.
 * The steps align with evidence preservation, compliance, and analytics module requirements.
 *
 * Steps:
 * 1. Create multiple webhook events with diverse integration_channel_id, event_type, destination_url, delivery_status, and created_at fields.
 * 2. Search/filter webhook events by creation date range and destination_url, and delivery_status, confirming all results match criteria.
 * 3. Test pagination by limiting results per page, requesting different pages, and verifying metadata and non-overlapping subsets.
 * 4. For each search, assert that all returned events match expected criteria, and pagination headers are consistent with created data.
 */
export async function test_api_webhook_event_search_by_criteria(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create webhook events with diverse fields
  const now = new Date();
  const integrationChannels = [
    typia.random<string & typia.tags.Format<"uuid">>(),
    typia.random<string & typia.tags.Format<"uuid">>(),
  ];
  const destinationUrls = [
    "https://endpoint-1.example/webhooks",
    "https://endpoint-2.example/webhooks",
  ];
  const eventTypes = ["order.updated", "payment.completed", "shipment.failed"];
  const deliveryStatuses = ["pending", "success", "failed"] as const;
  const createdEvents: IWebhookEvent[] = [];

  for (let i = 0; i < 8; ++i) {
    const event: IWebhookEvent = await api.functional.apiOmnichannel.webhookEvents.post(connection, {
      body: {
        integration_channel_id: integrationChannels[i % integrationChannels.length],
        event_type: eventTypes[i % eventTypes.length],
        destination_url: destinationUrls[i % destinationUrls.length],
        event_hash: typia.random<string>(),
      } satisfies IWebhookEvent.ICreate,
    });
    typia.assert(event);
    createdEvents.push(event);
  }

  // For better control, set custom creation range for filter
  const earliestCreated = createdEvents[0].created_at;
  const latestCreated = createdEvents[createdEvents.length - 1].created_at;

  // 2. Search by creation date range
  {
    const res: IPageIWebhookEvent = await api.functional.apiOmnichannel.webhookEvents.patch(connection, {
      body: {
        created_from: earliestCreated,
        created_to: latestCreated,
      } satisfies IWebhookEvent.IRequest,
    });
    typia.assert(res);
    for (const e of res.data) {
      TestValidator.equals("created window - from")(true)(e.created_at >= earliestCreated);
      TestValidator.equals("created window - to")(true)(e.created_at <= latestCreated);
    }
  }

  // 3. Search/filter by destination_url
  {
    const url = destinationUrls[0];
    const res: IPageIWebhookEvent = await api.functional.apiOmnichannel.webhookEvents.patch(connection, {
      body: {
        destination_url: url,
      } satisfies IWebhookEvent.IRequest,
    });
    typia.assert(res);
    for (const e of res.data) {
      TestValidator.equals("destination_url")(url)(e.destination_url);
    }
  }

  // 4. Search/filter by delivery_status
  for (const status of deliveryStatuses) {
    const res: IPageIWebhookEvent = await api.functional.apiOmnichannel.webhookEvents.patch(connection, {
      body: {
        delivery_status: status,
      } satisfies IWebhookEvent.IRequest,
    });
    typia.assert(res);
    for (const e of res.data) {
      TestValidator.equals("delivery_status")(status)(e.delivery_status);
    }
  }

  // 5. Pagination tests
  {
    const limit = 3;
    // Get first page
    const page1 = await api.functional.apiOmnichannel.webhookEvents.patch(connection, {
      body: { limit, page: 1 } satisfies IWebhookEvent.IRequest,
    });
    typia.assert(page1);
    TestValidator.equals("limit")(limit)(page1.pagination.limit);
    TestValidator.equals("current page")(1)(page1.pagination.current);
    TestValidator.equals("data.length <= limit")(true)(page1.data.length <= limit);

    // Get second page
    const page2 = await api.functional.apiOmnichannel.webhookEvents.patch(connection, {
      body: { limit, page: 2 } satisfies IWebhookEvent.IRequest,
    });
    typia.assert(page2);
    TestValidator.equals("limit")(limit)(page2.pagination.limit);
    TestValidator.equals("current page")(2)(page2.pagination.current);
    TestValidator.equals("data.length <= limit")(true)(page2.data.length <= limit);

    // Ensure no overlap between page 1 and page 2
    const ids1 = page1.data.map((e) => e.id);
    const ids2 = page2.data.map((e) => e.id);
    TestValidator.equals("no overlap between pages")(
      0,
    )([...ids1, ...ids2].length - new Set([...ids1, ...ids2]).size);
  }
}

