  const createdTags: IProductTag[] = [];
  for (const value of tagValues) {
    const tag = await api.functional.productCatalog.productTags.post(connection, {
      body: { value } satisfies IProductTag.ICreate,
    });
    typia.assert(tag);
    createdTags.push(tag);
  }

  // 2. List all tags with no filter and a large limit
  const allTags = await api.functional.productCatalog.productTags.patch(connection, {
    body: { limit: 10 },
  });
  typia.assert(allTags);
  TestValidator.equals("all tags count")(tagValues.length)(allTags.data.length);
  tagValues.forEach(val => TestValidator.equals("tag value exists")(true)(allTags.data.some(tag => tag.value === val)));

  // 3. Search by partial value
  const partial = "Eco";
  const ecoTags = await api.functional.productCatalog.productTags.patch(connection, {
    body: { value: partial },
  });
  typia.assert(ecoTags);
  // Must contain only tags with 'Eco' in value
  for (const tag of ecoTags.data)
    TestValidator.equals("partial match")(true)(tag.value.includes(partial));

  // 4. Pagination tests
  const limit = 2;
  const page1 = await api.functional.productCatalog.productTags.patch(connection, {
    body: { limit, page: 1 },
  });
  const page2 = await api.functional.productCatalog.productTags.patch(connection, {
    body: { limit, page: 2 },
  });
  typia.assert(page1); typia.assert(page2);
  TestValidator.equals("page1.size")(limit)(page1.data.length);
  // page2: size can be <= limit (last page)
  TestValidator.equals("page2.size")(true)(page2.data.length <= limit);
  // Ensure no duplicate IDs across pages
  page1.data.forEach(tag1 => TestValidator.equals("not in page2")(false)(page2.data.some(tag2 => tag2.id === tag1.id)));

  // 5. Paginate beyond available pages (should be empty)
  const totalPages = Math.ceil(tagValues.length / limit);
  const tooFar = await api.functional.productCatalog.productTags.patch(connection, {
    body: { limit, page: totalPages + 2 },
  });
  typia.assert(tooFar);
  TestValidator.equals("empty out-of-bounds")(0)(tooFar.data.length);

  // 6. (TODO) Soft deletion test: If an API exists to soft-delete tags, delete & re-query
  // Skipped here, as provided SDK does not include a delete endpoint in this example.
}

