import { IProductMedia } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductMedia";

/**
 * Test listing/searching product media records with various filter criteria and pagination to ensure accurate results.
 *
 * 1. Create multiple product media records with different media_type, locale, and alt_text fields.
 * 2. List/search with filters (media_type, locale, alt_text, product_id, created_from/to).
 * 3. Assert that results match expected filtered records, not others.
 * 4. Paginate with limit/offset and validate correct slicing and pagination metadata.
 * 5. Simulate 'deletion/archiving' of media (if API supported; if not, skip this step) and confirm such records do not appear in listings.
 * 6. Paginate past available data and verify empty result set and correct pagination metadata.
 */
export async function test_api_product_media_list_search_with_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: Create several product media records with distinct fields for filtering
  const mediaRecords: IProductMedia[] = [];
  for (let i = 0; i < 5; ++i) {
    const media_type = i % 2 === 0 ? "image" : "video";
    const locale = i % 2 === 0 ? "en-US" : "ko-KR";
    const alt_text = `Test Media Alt Text ${i}`;
    const uri = `/sample/uri/${i}`;
    const product_id = typia.random<string & typia.tags.Format<"uuid">>();
    const body = {
      product_id,
      uri,
      media_type,
      locale,
      alt_text,
    } satisfies IProductMedia.ICreate;
    const created = await api.functional.productCatalog.productMedia.post(connection, { body });
    typia.assert(created);
    mediaRecords.push(created);
  }

  // Step 2: List/search with specific filters - by media_type
  for (const type of ["image", "video"]) {
    const output: IPageIProductMedia = await api.functional.productCatalog.productMedia.patch(connection, {
      body: { media_type: type, limit: 10 },
    });
    typia.assert(output);
    for (const media of output.data) {
      TestValidator.equals(`media_type:${type}`)(type)(media.media_type);
    }
  }

  // Step 3: List/search with locale filter
  for (const locale of ["en-US", "ko-KR"]) {
    const output: IPageIProductMedia = await api.functional.productCatalog.productMedia.patch(connection, {
      body: { locale, limit: 10 },
    });
    typia.assert(output);
    for (const media of output.data) {
      TestValidator.equals(`locale:${locale}`)(locale)(media.locale);
    }
  }

  // Step 4: List/search with alt_text partial search
  const altTextTerm = "Alt Text 1";
  {
    const output: IPageIProductMedia = await api.functional.productCatalog.productMedia.patch(connection, {
      body: { alt_text: altTextTerm, limit: 10 },
    });
    typia.assert(output);
    for (const media of output.data) {
      TestValidator.true(`alt_text includes '${altTextTerm}'`)(media.alt_text.includes(altTextTerm));
    }
  }

  // Step 5: List/search by product_id
  {
    const media = mediaRecords[0];
    const output: IPageIProductMedia = await api.functional.productCatalog.productMedia.patch(connection, {
      body: { product_id: media.product_id, limit: 10 },
    });
    typia.assert(output);
    for (const media2 of output.data) {
      TestValidator.equals(`product_id:${media.product_id}`)(media.product_id)(media2.product_id);
    }
  }

  // Step 6: Pagination test - limit/offset
  {
    const output: IPageIProductMedia = await api.functional.productCatalog.productMedia.patch(connection, {
      body: { limit: 2, offset: 0, sort: "created_at asc" },
    });
    typia.assert(output);
    TestValidator.equals("limit=2")(2)(output.data.length);
    TestValidator.equals("current page")(0)(output.pagination.current);
  }
  {
    const output: IPageIProductMedia = await api.functional.productCatalog.productMedia.patch(connection, {
      body: { limit: 2, offset: 4, sort: "created_at asc" },
    });
    typia.assert(output);
    // Expect one record in the last page (since we created 5 above)
    TestValidator.true("0 or 1 record on last page")(output.data.length <= 1);
  }

  // Step 7: Paginate past available media and verify empty set
  {
    const output: IPageIProductMedia = await api.functional.productCatalog.productMedia.patch(connection, {
      body: { limit: 10, offset: 1000 },
    });
    typia.assert(output);
    TestValidator.equals("empty data[] beyond last page")(0)(output.data.length);
  }
}

