          stock_count: 100 + i,
          last_modified_at: new Date(Date.now() - i * 86400000).toISOString(),
        } satisfies IInventory.ICreate,
      },
    );
    typia.assert(created);
    inventories.push(created);
  }

  // 2.      inventory 
  // 2-1.  (product_variant_id) 
  const filterByVariant = await api.functional.productCatalog.inventory.patch(
    connection,
    {
      body: {
        product_variant_id: inventories[0].product_variant_id,
