  );
  typia.assert(filterByVariant);
  TestValidator.equals("variant filter count")(1)(filterByVariant.data.length);
  TestValidator.equals("filtered inventory id")(
    inventories[0].id
  )(filterByVariant.data[0].id);

  // 2-2. (stock_count)  
  const filterByStock = await api.functional.productCatalog.inventory.patch(
    connection,
    {
      body: {
        stock_from: 101,
        stock_to: 104,
      } satisfies IInventory.IRequest,
    },
  );
  typia.assert(filterByStock);
  TestValidator.equals("stock count filter count")(4)(filterByStock.data.length);

  // 2-3.  (last_modified_at ) 
  const now = new Date();
  const threeDaysAgo = new Date(now.getTime() - 3 * 86400000).toISOString();
  const filterByModified = await api.functional.productCatalog.inventory.patch(
    connection,
    {
      body: {
        last_modified_from: threeDaysAgo,
        last_modified_to: now.toISOString(),
      } satisfies IInventory.IRequest,
    },
  );
  typia.assert(filterByModified);
  TestValidator.equals("last_modified filter count")(4)(filterByModified.data.length);

  // 3. (      )
  //      /    ,      
  //     [ ,     assert  ]
  //
  // 4.  -             ,    
  const pageLimit = 2;
  const allListed: IInventory[] = [];
  let offset = 0;
  while (true) {
