    if (page.data.length === 0) break;
    allListed.push(...page.data);
    offset += pageLimit;
  }
  //      
  const uniqueIds = new Set(allListed.map((i) => i.id));
  TestValidator.equals("    ")(inventories.length)(uniqueIds.size);
  //    :  
  const pastLast = await api.functional.productCatalog.inventory.patch(connection, {
    body: {
      offset: 999,
      limit: 100,
    } satisfies IInventory.IRequest,
  });
  typia.assert(pastLast);
  TestValidator.equals("empty page at past limit")(0)(pastLast.data.length);
