import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IInventory } from "@ORGANIZATION/PROJECT-api/lib/structures/IInventory";

/**
 * Validates the retrieval of a specific inventory record by ID and error on non-existent ID.
 *
 * This test ensures that inventory can be retrieved successfully via its UUID and that the system properly handles requests for unknown or deleted inventory records.
 * The scenario covers both the positive flow (valid record) and the negative/error flow.
 *
 * 1. Create a valid inventory record using the API and obtain its ID for the positive retrieval test.
 * 2. Retrieve the record by its ID and validate all fields with typia.assert.
 * 3. Attempt to retrieve a record using a random UUID (ensured to not exist), expect a 404 or appropriate error response.
 *
 * This covers expected behaviors for both normal and error conditions on the inventory/getById endpoint.
 */
export async function test_api_productCatalog_inventory_getById_success_and_error(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create inventory record
  const inventory: IInventory = await api.functional.productCatalog.inventory.post(connection, {
    body: {
      product_variant_id: typia.random<string & typia.tags.Format<"uuid">>(),
      stock_count: 100,
      last_modified_at: new Date().toISOString(),
    } satisfies IInventory.ICreate,
  });
  typia.assert(inventory);

  // 2. Retrieve inventory by ID
  const fetched: IInventory = await api.functional.productCatalog.inventory.getById(connection, {
    id: inventory.id,
  });
  typia.assert(fetched);

  // 3. Try to retrieve by a random/unknown ID (should 404 or error)
  const badId: string & typia.tags.Format<"uuid"> = typia.random<string & typia.tags.Format<"uuid">>();
  if (badId !== inventory.id) {
    await TestValidator.httpError("inventory not found error")(404)(() =>
      api.functional.productCatalog.inventory.getById(connection, { id: badId }),
    );
  } else {
    // Rare collision: generate again
    await TestValidator.httpError("inventory not found error")(404)(() =>
      api.functional.productCatalog.inventory.getById(connection, { id: typia.random<string & typia.tags.Format<"uuid">>() }),
    );
  }
}

import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IInventory } from "@ORGANIZATION/PROJECT-api/lib/structures/IInventory";
import { IProductCatalogProductVariant } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductCatalogProductVariant";

/**
 * Validate creation and constraints of inventory records (inventory table).
 *
 * This test validates the following:
 * - That a valid inventory record can be created for a product variant (with valid variant id, positive stock and optional timestamp)
 * - That creation with missing or invalid fields (missing variant id, negative stock, etc) is rejected with validation errors
 * - That duplicate creation (same product_variant_id) fails with uniqueness/conflict error
 *
 * Steps:
 * 1. Create a dummy product variant entity to use for inventory creation
 * 2. Attempt to create a new inventory record for this variant with valid stock count and required fields  expect success
 * 3. Attempt to create the same inventory but missing product_variant_id  expect validation error
 * 4. Attempt to create with negative stock_count  expect validation error
 * 5. Attempt to create a duplicate inventory for the same product_variant_id  expect conflict/duplicate error
 */
export async function test_api_inventory_create_constraints_validation(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a dummy product variant (simulate existing valid variant)
  const dummyVariant: IProductCatalogProductVariant = {
    id: typia.random<string>(),
    product_id: typia.random<string>(),
    sku: "SKU-TEST-" + Math.random().toString(36).slice(2, 7),
    price: 19999,
    barcode: "BARCODE-" + Math.random().toString(36).slice(2, 8),
    available: true,
    created_at: new Date().toISOString(),
    updated_at: new Date().toISOString(),
  };

  // 2. Create inventory with valid fields
  const validPayload = {
    product_variant_id: dummyVariant.id,
    stock_count: 10,
    last_modified_at: new Date().toISOString(),
  } satisfies IInventory.ICreate;

  const inventory: IInventory = await api.functional.productCatalog.inventory.post(
    connection,
    {
      body: validPayload,
    },
  );
  typia.assert(inventory);
  TestValidator.equals("product_variant_id")(validPayload.product_variant_id)(inventory.product_variant_id);
  TestValidator.equals("stock_count")(validPayload.stock_count)(inventory.stock_count);

  // 3. Attempt creation missing product_variant_id (should fail validation)
  await TestValidator.httpError("missing product_variant_id")(400)(() =>
    api.functional.productCatalog.inventory.post(connection, {
      body: {
        // @ts-expect-error
        stock_count: 5,
        last_modified_at: new Date().toISOString(),
      } as any,
    }),
  );

  // 4. Attempt creation with negative stock_count (should fail)
  await TestValidator.httpError("negative stock_count")(400)(() =>
    api.functional.productCatalog.inventory.post(connection, {
      body: {
        product_variant_id: dummyVariant.id,
        stock_count: -10,
        last_modified_at: new Date().toISOString(),
      } as any,
    }),
  );

  // 5. Attempt duplicate creation for same variant (uniqueness constraint)
  await TestValidator.httpError("duplicate inventory creation")(409)(() =>
    api.functional.productCatalog.inventory.post(connection, {
      body: validPayload,
    }),
  );
}

