import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IImportFeed } from "@ORGANIZATION/PROJECT-api/lib/structures/IImportFeed";
import { IPageIImportFeed } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIImportFeed";
import { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";

/**
 * Test searching and listing import feed records with various filters and pagination.
 *
 * This test verifies that the import feed search API correctly filters by source_name and imported_at (date window),
 * supports pagination, returns empty results for out-of-range pages, and that deleted feeds do not show up in the results.
 *
 * Steps:
 * 1. Create multiple (>=5) unique import feed records with different source_name and imported_at values.
 * 2. Search for all feeds, verify all records returned via pagination.
 * 3. Filter feeds by source_name, verify only matching feeds are returned.
 * 4. Filter feeds by imported_at date range to verify correct date filtering.
 * 5. Paginate with a page/limit smaller than total and verify correct slicing.
 * 6. Search with a page number beyond the last page, expect empty results.
 * 7. (If soft-deletion supported) Mark one feed as deleted (simulate or note in reality) and search again to confirm it's absent.
 */
export async function test_api_import_feed_list_search_filters_pagination(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: Create multiple import feed records
  const feeds: IImportFeed[] = [];
  const baseDate = new Date();
  for (let i = 0; i < 6; ++i) {
    const date = new Date(baseDate);
    date.setDate(date.getDate() - i); // Stagger dates
    const record = await api.functional.productCatalog.importFeeds.post(connection, {
      body: {
        source_name: `source_test_${i % 2 === 0 ? "A" : "B"}`,
        source_uri: `https://test.uri/feed${i}`,
        imported_at: date.toISOString(),
      } satisfies IImportFeed.ICreate,
    });
    typia.assert(record);
    feeds.push(record);
  }

  // Step 2: List all, verify all are returned
  let result: IPageIImportFeed = await api.functional.productCatalog.importFeeds.patch(connection, {
    body: {
      sort: "imported_at-desc",
      limit: 100,
      page: 1,
    } satisfies IImportFeed.IRequest,
  });
  typia.assert(result);
  const ids = feeds.map(f => f.id);
  const listedIds = (result.data ?? []).filter(r => ids.includes(r.id)).map(r => r.id);
  TestValidator.equals("all feeds present")(ids.sort())(listedIds.sort());

  // Step 3: Filter by source_name
  const sourceA = feeds.filter(f => f.source_name.endsWith("A"));
  result = await api.functional.productCatalog.importFeeds.patch(connection, {
    body: {
      source_name: "source_test_A",
      sort: "imported_at-desc",
      limit: 100,
      page: 1,
    },
  });
  typia.assert(result);
  const dataA = (result.data ?? []);
  TestValidator.equals("source_name=A count")(sourceA.length)(dataA.length);
  for (const f of dataA) TestValidator.equals("source_name filter")(f.source_name)("source_test_A");

  // Step 4: Filter by imported_at from window (most recent 3)
  const windowStart = new Date(baseDate); windowStart.setDate(windowStart.getDate() - 2);
  result = await api.functional.productCatalog.importFeeds.patch(connection, {
    body: {
      imported_from: windowStart.toISOString(),
      limit: 100,
      page: 1,
    },
  });
  typia.assert(result);
  const dataWin = (result.data ?? []);
  TestValidator.equals("window count")(3)(dataWin.filter(r => ids.includes(r.id)).length);

  // Step 5: Paginate, limit = 2
  result = await api.functional.productCatalog.importFeeds.patch(connection, {
    body: {
      limit: 2,
      page: 2,
    },
  });
  typia.assert(result);
  const pagination = result.pagination;
  if (pagination) {
    TestValidator.equals("pagination limit")(2)(pagination.limit);
    TestValidator.equals("pagination page")(2)(pagination.current);
  }

  // Step 6: Out-of-range page
  const totalPages = pagination?.pages ?? 4;
  result = await api.functional.productCatalog.importFeeds.patch(connection, {
    body: {
      limit: 2,
      page: totalPages + 1,
    },
  });
  typia.assert(result);
  TestValidator.equals("out of range empty")((result.data ?? []).length)(0);

  // Step 7: Deleted feed absent (simulate)
  // Note: If API supports soft deleting, add a delete step and verify absence.
  // Otherwise, skip this test step.
}

