 * 4. Attempt to create a duplicate import feed (same source_name or source_uri, should error by uniqueness constraint).
 *
 * This ensures both positive and negative test coverage, including expected constraint violations and system validation.
 */
export async function test_api_import_feed_create_validation_and_duplicate(
  connection: api.IConnection,
): Promise<void> {
  // 1. Successfully create new import feed
  const now = new Date().toISOString();
  const importFeedInput: IImportFeed.ICreate = {
    source_name: "Google Shopping",
    source_uri: "https://feeds.google.com/base/partner123.xml",
    imported_at: now,
  };

