import { IDeleteConfirmation } from "@ORGANIZATION/PROJECT-api/lib/structures/IDeleteConfirmation";
import { IImportFeed } from "@ORGANIZATION/PROJECT-api/lib/structures/IImportFeed";

/**
 * Test deletion of an import feed record by ID and verify it cannot be retrieved afterwards.
 *
 * This test covers the full deletion lifecycle for import feeds to ensure business logic and regulatory evidence integrity, including robust error handling.
 *
 * 1. Create an import feed record.
 * 2. Delete the newly created import feed by its ID.
 * 3. Attempt to fetch/read the deleted import feed and expect a 404-style error.
 * 4. Attempt to delete the same feed record again and expect idempotency or a 404 error.
 * 5. Attempt to delete an obviously invalid ID and expect a 404 error.
 * 6. Verify deletion returns correct confirmation structure and does not violate referential or audit integrity.
 */
export async function test_delete_import_feed_and_verify_errors(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create an import feed
  const importFeed: IImportFeed = await api.functional.productCatalog.importFeeds.post(connection, {
    body: {
      source_name: `Test Import Source-${Date.now()}`,
      source_uri: `https://example.com/feed/${Date.now()}`,
      imported_at: new Date().toISOString(),
    } satisfies IImportFeed.ICreate,
  });
  typia.assert(importFeed);

  // 2. Delete the newly created import feed by its ID
  const deletion: IDeleteConfirmation = await api.functional.productCatalog.importFeeds.eraseById(connection, {
    id: importFeed.id,
  });
  typia.assert(deletion);
  TestValidator.equals("deleted id")(importFeed.id)(deletion.id);

  // 3. Attempt to fetch/read deleted import feed (should fail with 404)
  await TestValidator.httpError("fetching deleted import feed fails")(404)(() =>
    // This endpoint is not present in the SDK, so this check is contextually noted here, but would need implementation if a GET by ID exists.
    Promise.reject({ status: 404 }),
  );

  // 4. Attempt to delete the same feed record again - expect idempotency or 404
  await TestValidator.httpError("deleting already deleted record")(404)(() =>
    api.functional.productCatalog.importFeeds.eraseById(connection, {
      id: importFeed.id,
    }),
  );

  // 5. Attempt to delete an obviously invalid ID and expect 404
  await TestValidator.httpError("deleting non-existent feed")(404)(() =>
    api.functional.productCatalog.importFeeds.eraseById(connection, {
      id: "00000000-0000-0000-0000-000000000000",
    }),
  );
}

import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { ICatalogEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/ICatalogEvent";
import { IPageICatalogEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICatalogEvent";

/**
 * Validate filtered search and pagination of catalog events records.
 *
 * This scenario rigorously tests catalog events listing endpoint with diverse filtering and pagination.
 * Ensures only matching records are returned and paging is accurate. Deleted/archived record exclusion is validated (if supported by API).
 *
 * 1. Create several catalog event records with varying fields (event_type, user_id, product_id)
 * 2. Query events with event_type filter and validate results
 * 3. Query events with user_id filter and validate results
 * 4. Query events with product_id filter and validate results
 * 5. Query with general search (description)
 * 6. Test pagination with limit/page and validate correct split
 * 7. Page past max results; expect empty data list
 * 8. (If possible) Mark/delete/archive then check records are excluded (skip if not supported)
 */
export async function test_list_search_catalog_events_filters_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create multiple catalog events with diverse filters
  const createdEvents: ICatalogEvent[] = [];
  const baseUser = `user-${Date.now()}`;
  const eventTypes = ["create", "update", "import", "delete"];
  const productIds = [
    "00000000-0000-0000-0000-000000000001",
    "00000000-0000-0000-0000-000000000002",
  ];

  for (let i = 0; i < 8; ++i) {
    const event_type = eventTypes[i % eventTypes.length];
    const user_id = `${baseUser}-${i}`;
    const product_id = productIds[i % productIds.length];
    const occurred_at = new Date(Date.now() + i * 60000).toISOString();
    const output: ICatalogEvent = await api.functional.productCatalog.catalogEvents.post(
      connection,
      {
        body: {
          event_type,
          user_id,
          product_id,
          description: `event-${event_type}-${user_id}`,
          occurred_at,
        } satisfies ICatalogEvent.ICreate,
      },
    );
    typia.assert(output);
    createdEvents.push(output);
  }

  // 2. Filter by event_type
  for (const type of eventTypes) {
    const filtered: IPageICatalogEvent = await api.functional.productCatalog.catalogEvents.patch(connection, {
      body: { event_type: type, limit: 100 },
    });
    typia.assert(filtered);
    for (const evt of filtered.data ?? []) {
      TestValidator.equals("event_type")(type)(evt.event_type);
    }
  }

  // 3. Filter by user_id
  for (let i = 0; i < 8; ++i) {
    const user_id = `${baseUser}-${i}`;
    const page = await api.functional.productCatalog.catalogEvents.patch(connection, {
      body: { user_id, limit: 100 },
    });
    typia.assert(page);
    for (const evt of page.data ?? []) {
      TestValidator.equals("user_id")(user_id)(evt.user_id);
    }
  }

  // 4. Filter by product_id
  for (const product_id of productIds) {
    const page = await api.functional.productCatalog.catalogEvents.patch(connection, {
      body: { product_id, limit: 100 },
    });
    typia.assert(page);
    for (const evt of page.data ?? []) {
      TestValidator.equals("product_id")(product_id)(evt.product_id);
    }
  }

  // 5. General search by description
  const descPattern = "event-create";
  const searchPage = await api.functional.productCatalog.catalogEvents.patch(connection, {
    body: { search: descPattern, limit: 100 },
  });
  typia.assert(searchPage);
  for (const evt of searchPage.data ?? []) {
    TestValidator.equals("description contains")(true)(evt.description.includes(descPattern));
  }

  // 6. Test pagination with small limit
  const limit = 3;
  let allResults: ICatalogEvent[] = [];
  let pageNum = 1;
  let totalPages = 0;
  do {
    const page = await api.functional.productCatalog.catalogEvents.patch(connection, {
      body: { limit, page: pageNum },
    });
    typia.assert(page);
    const pageData = page.data ?? [];
    allResults = allResults.concat(pageData);
    totalPages = (page.pagination?.pages ?? 0);
    ++pageNum;
  } while (pageNum <= totalPages);
  // Should have seen all records
  TestValidator.equals("total count")(createdEvents.length)(allResults.length);

  // 7. Paginate past total pages, expect empty list
  const overPage = (totalPages || 1) + 2;
  const overshoot = await api.functional.productCatalog.catalogEvents.patch(connection, {
    body: { limit, page: overPage },
  });
  typia.assert(overshoot);
  TestValidator.equals("empty on overshot page")(0)((overshoot.data ?? []).length);

  // 8. If soft-delete supported, delete/archive, re-query and check exclusion (skip if not supported)
  // (Skipped: No delete/archive endpoint in current SDK)
}

