import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { ICatalogEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/ICatalogEvent";

/**
 * Validate creation of catalog event records, input validation, and uniqueness constraint enforcement.
 *
 * 1. Create a valid catalog event with all necessary/optional fields.
 * 2. Attempt creation with missing required fields and expect validation errors.
 * 3. Attempt to create a duplicate catalog event (same unique constraint) and expect a constraint violation error.
 */
export async function test_api_catalog_event_create_and_constraint(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a valid catalog event
  const now = new Date();
  const eventBody: ICatalogEvent.ICreate = {
    // Note some fields are optional.
    user_id: typia.random<string>(),
    event_type: "create",
    description: "Product created by admin user.",
    occurred_at: now.toISOString(),
    // product_id, product_variant_id are optional
  };
  const created: ICatalogEvent = await api.functional.productCatalog.catalogEvents.post(
    connection,
    { body: eventBody }
  );
  typia.assert(created);
  TestValidator.equals("event_type")(eventBody.event_type)(created.event_type);
  TestValidator.equals("user_id")(eventBody.user_id)(created.user_id);
  TestValidator.equals("description")(eventBody.description)(created.description);

  // 2. Attempt with missing required fields (user_id missing)
  await TestValidator.httpError("missing user_id validation")(400)(() =>
    api.functional.productCatalog.catalogEvents.post(connection, {
      body: {
        ...eventBody,
        user_id: undefined as any, // omit
      },
    })
